vec3 pattern( in vec2 uv )
{   vec3 col = vec3(0.45); 
    vec3 col2 = 0.5 * cos(iTime + uv.xyx*0.05 + vec3(0,2,4));  
    col += 0.5*smoothstep(-0.05,0.06,cos(uv.x*0.4)*cos(uv.y*0.4))*col2; 
