#define rot(a) mat2( cos( a + vec4(0,11,33,0) ) )

void mainImage( out vec4 O, vec2 u )
{
	float t = iTime, dt = .035, s,c,f, i=0.;
    vec2  R = iResolution.xy,
          q = ( u+u - R ) / R.y;

    vec3 P = vec3(0), D = normalize(vec3(q,3) ), l = vec3(1), p;
    D.yz *= rot(-.15); 
    D.xz *= rot(.1*t);
    P.y += 2.;        
    P.x -=t*.4;

    t = 2.5;                         
    for( O *= 0.; i++<1e2;  ) {                
        p = P + t*D;
        if(p.y<0.)D.y = -D.y,P = p,t = 1.;  	
        for(  s = 1., f = p.y, c = 1.7-f ; s < 8e2; s *= 1.7 )
                p.xz *= rot(1.),
                c += abs( dot(sin(p*s),l)) / s,
                f += dot(sin(p.xz*.4*s),l.xz) / s;

        t  += dt * min(2.*f,max(c,.05));
        dt *= 1.036;
        s = .3/(1.+exp(-c));
        c = 1. - min( .2* ( f>.001 ? c : s) , 1.);
        f = 1. - s;
        O = .9*O + .05* c*vec4(c*c*(f+.6*c),c*.5*(f-c+3.), f+.2, 0) *(.8 + .3*t);	
    }  

    O =  .5 * log(1.+O);           
}
