float f_n_inoutexp_bad(float n, float n_sharpness){
    n_sharpness = floor(n_sharpness) + mod(floor(n_sharpness),2.); // must be dividable by 2.
    n = max(0.,min(1., n));
    return min(pow(n, n_sharpness),pow( (n-1.), n_sharpness))*pow(2., abs(n_sharpness));
}
float f_n_inoutexp(float n, float n_sharpness){
    n_sharpness = floor(n_sharpness) + mod(floor(n_sharpness),2.); // must be dividable by 2.
    n = max(0.,min(1., n));
    return min(pow(n, n_sharpness),pow( abs(n-1.), n_sharpness))*pow(2., abs(n_sharpness));
}
float f_n_dist_square(vec2 o){
    return max(abs(o.x), abs(o.y));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono_nooffset = (iMouse.xy )/ iResolution.y;
    o_trpino*=1.2;
    float n_tau = 6.2831;
    float n_its = 20.;
    float n_index = floor(o_trpino.x*n_its);
    float n_index_nor = n_index / n_its;
    float n_dist_clst = 0.;
    float n_t = iTime*2.;
    float n_mixed = 0.;
    float n_dist = 0.5;
    float n_col_nor = 0.;
    for(float n_it = 0.; n_it < n_its; n_it+=1.){
        float n_it_nor = n_it/ n_its;

        float n_amp = 1./n_its/2.;

        o_trpino += vec2(
            //n_it_nor*0.1
            sin(n_t+n_it_nor*n_tau*n_t)*n_amp, 
            cos(n_t+n_it_nor*n_tau*n_t)*n_amp
        );


        float n = 3.;
        //n = (sin(n_t+n_it*0.2)*0.5+0.5)*5.+1.;
        //float n_dist = length(pow(o_trpino,vec2(n))*pow(n, n));
        n_dist = pow((pow(abs(o_trpino.x), n))+(pow(abs(o_trpino.y), abs(n))), 1./n);
        n_dist = f_n_dist_square(o_trpino);
        //n_dist *= 10.-(floor(abs(o_trpino.x)*10.));
        //n_dist *=(n_it*0.01);
        n_dist += n_it*(1./n_its/2.);
        //n_dist = length(o_trpino);
        if(o_trpino.x>0.){
            n_dist = f_n_inoutexp_bad(n_dist, 8.);
        }else{
            n_dist = f_n_inoutexp(n_dist, 8.);
        }
        n_mixed = mix(n_mixed, n_dist, n_dist);
        if(n_dist > n_dist_clst){
            n_col_nor = fract((n_it/n_its)+n_t*0.1);
        }
        n_dist_clst = max(n_dist_clst, n_dist);

    }

    fragColor = vec4(
        n_dist_clst
    );

}
