float f_n_dist_1(vec2 o_p){

    vec2 o_abs = abs(o_p)-.5;
    float n = max(o_abs.x, o_abs.y*o_abs.x*10.)-.1;

    return n;
}
float f_n_dist_square(vec2 o_p){

    vec2 o_abs = abs(o_p);
    float n = max(o_abs.x, o_abs.y)-.5;

    return n;
}

float f_n_dist_circ(vec2 o_p){

    return length(o_p)-.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trn_p = (fragCoord.xy - iResolution.xy*.5)/ iResolution.y;
    o_trn_p *= 2.;
    float n_dc = f_n_dist_circ(o_trn_p);
    float n_d1 = f_n_dist_1(o_trn_p);

    float n_t = sin(iTime)*.5+0.5;
    float n_d = 
        (1.-n_t) * n_dc 
        + n_t * n_d1; 

    vec3 o_col_signed = vec3(0.2, 0.4, 0.1);
    vec3 o_col_unsigned = vec3(0.8, 0.8, 0.1);


    float b = float(n_d < 0.);
    float n_dabs = abs(n_d);
    vec3 o_col = 
        (1.-b)* o_col_unsigned + 
        b * o_col_signed;

    float n_y = sin(n_dabs*200.);
    float n_y_abs = abs(n_y);

    float b_n_y = float(n_y < 0.);

    vec3 o_col2 = 
        (1.-b_n_y)* o_col + 
        b_n_y * (o_col * 0.6);

    // o_col2 *= (n_dabs)*5.;

    // float n_dwhite = pow(1.-n_dabs, 10.)*.8;
    float n_thick = 0.005;
    float n_aa = 1./ min(iResolution.x, iResolution.y)*5.;
    float n_dwhite = smoothstep(n_thick+n_aa, n_thick ,n_dabs);

    // fragColor = vec4(o_col2, 1.);
    fragColor = vec4(
        n_dwhite 
        + o_col2
        , 1.);
}
