float plotline(in mat2 pts,in vec2 xy){ //x1,y1,x2,y2
    xy.x=xy.x*iResolution.x/iResolution.y;
    float den1=pts[0][0]-pts[1][0];
    float den2=pts[1][0]-pts[0][0];
    float up1=xy.x-pts[1][0];
    float up2=xy.x-pts[0][0];
    float liney=pts[0][1]*up1/den1+pts[1][1]*up2/den2;
    return step(1.-(fwidth(xy.x)+fwidth(xy.y))*(.65+0.3*cos(.4*iTime)),1.-abs(liney-xy.y));

}                                                                                

float maz(vec2 uv,float t){
    vec2 mazingpt=vec2(cos(sin(tan(t)+t)+t),sin(cos(tan(pow(2.,1./2.)*t)+t)+t));
    return plotline(mat2(vec2(sin(t),cos(t))*0.8*tan(0.2*t),mazingpt),vec2(uv.x,uv.y));;
}

