// Made by StoneTrench

int seedSize = 16;

float smoothStep(float x){
    float x3 = x * x * x;
    return 6.0 * (x3 * x * x) - 15.0 * (x3 * x) + 10.0 * (x3);
}
float lerp(float a, float b, float t){
    return a + t * (b - a);
}


float sampleSeed(float _x, float _y){
    vec2 pF = mod(abs(vec2(_x, _y)), float(seedSize));

    int x = int(pF.x);
    int y = int(pF.y);

    return seed[x + (y * seedSize)] / 10.0f;
}

float sampleNoise(vec2 p){
    float scale = 100.0f;
    vec2 s = p / scale;
    vec2 f = floor(s);
    vec2 c = s;

    float s0 = sampleSeed(f.x, f.y);
    float s1 = sampleSeed(f.x + 1.0f, f.y);
    float s2 = sampleSeed(f.x, f.y + 1.0f);
    float s3 = sampleSeed(f.x + 1.0f, f.y + 1.0f);

    float lx = lerp(s0, s1, (c.x - f.x));
    float ly = lerp(s2, s3, (c.x - f.x));

    return lerp(lx, ly, (c.y - f.y));
}

float Octaves(vec2 fragCoord, int octaves){
    float result = 0.0;

    float b = 1.0;
    for (int i = 0; i < octaves; i++){
        b *= 2.0;
        result += sampleNoise(b * fragCoord);
    }

    return sin(result / float(octaves));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 100.0;
    float t = iTime / r * 100.0;
    float noise = abs(Octaves(fragCoord + vec2(sin(t), cos(t)) * r, 4));

    fragColor = vec4(1.0) * noise;
}
