#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define smoothstep(a, b, x) mix(a, b, smoothstep(0.0, 1.0, (x - a) / (b - a)))
float sdEquilateralTriangle( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}


float sdCircle(in vec2 p, in float r) {
return length(p) - r;
}

float sdSquare(in vec2 p, in float r) {
vec2 d = abs(p) - vec2(r);
return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

vec2 q = fragCoord/iResolution.xy;
q = -1.0 + 2.0*q;
q.x = abs(q.x);

float time = mod(iTime, 10.0); // loop 0 a 10

float transition = 0.0;
if (time < 5.0) {
transition = smoothstep(0.0, 5.0, time);
} else {
transition = smoothstep(5.0, 0.0, time - 5.0); // invertido
}

float d;
if (time < 5.0) {
d = mix(sdCircle(q, 0.5), sdSquare(q, 0.5), transition);
} else {
d = mix(sdSquare(q, 0.5), sdCircle(q, 0.5), transition);
}

vec3 col = vec3(0.0);
if (d < 0.0) {
col = 0.5 + sin(vec3(0.0, 1.0, 2.0) + d*10.0);
}

col += sin(d*10.0);
fragColor = vec4(col,1.0);
}
