// Found this on GLSL sandbox. I really liked it, changed a few things and made it tileable.
// :)
// by David Hoskins.
// Original water turbulence effect by joltz0r


// Redefine below to see the tiling...
//#define SHOW_TILING
const vec2 target_resolution = vec2(940, 86);

#define TAU 6.28318530718
#define MAX_ITER 5

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{

	float time = iTime * .5 + 23.0;
    // Same texCoord as in Minecraft
    vec2 texCoord = fragCoord/iResolution.xy;
    // Own uv variable
    vec2 uv = texCoord;

    // Center origin around screen center (range -0.5, 0.5)
    uv -= .5;

    // Squish image in excessive dimension
    float r = (iResolution.x/iResolution.y) / (target_resolution.x/target_resolution.y);
    if (r >= 1.) uv.x *= r;
    else uv.y /= r;

    // Recenter origin to start at 0 (range 0.0, 1.0)
    uv += .5;
    uv.y -= 0.5/r - 0.5; 

    vec2 uv0 = uv; 
    uv0.x *= 2.;
    //uv0.x += iTime * 0.3;
    uv0.y *= 1.;
    //uv0.y += iTime * 0.1;

    vec2 p = mod(uv0*TAU, TAU)-250.0;

	vec2 i = vec2(p);
	float c = 1.0;
	float inten = .009;

	for (int n = 0; n < MAX_ITER; n++) 
	{
		float t = time * (1.0 - (3.5 / float(n+1)));
		i = p + vec2(cos(t - i.x) + sin(t + i.y), sin(t - i.y) + cos(t + i.x));
		c += 1.0/length(vec2(p.x / (sin(i.x+t)/inten),p.y / (cos(i.y+t)/inten)));
	}

	c /= float(MAX_ITER);
    //1.1可以调整蓝色的深浅
	c = 1.17 - pow(c, 1.1);
	vec3 colour = vec3(pow(abs(c), 7.5));
    colour = clamp(colour + vec3(0.0, 0.35, 0.5), 0.0, 1.0);

	#ifdef SHOW_TILING
	// Flash tile borders...
	vec2 pixel = 2.0 / iResolution.xy;
	uv *= 2.0;
	float f = floor(mod(iTime*.5, 2.0)); 	// Flash value.
	vec2 first = step(pixel, uv) * f;		   	// Rule out first screen pixels and flash.
	uv  = step(fract(uv), pixel);				// Add one line of pixels per tile.
	colour = mix(colour, vec3(1.0, 1.0, 0.0), (uv.x + uv.y) * first.x * first.y); // Yellow line
	#endif

	fragColor = vec4(colour, 1.0);

    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    
}
