void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    O = texture(iChannel0, uv);

    float minD = 2.;
    float x = 0.;
    for (float i = 0.; i<r.x; i++){
        x = length(texture(iChannel0, texture(iChannel1, uv).xy/scale).xy - uv);
        if (x < minD){
            minD = x;
        }
    }

    O = vec4(1);
    O*= smoothstep(.0, 10./r.x, minD);

    O = texture(iChannel1, uv);
}

// Particle buffer

void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    if (uv.x * scale > 1. || uv.y * scale > 1.) return;

    if (iFrame < 30){
        O.xy = uv;
        O.zw = normalize(hash(uvec3(U, iDate.w)).xy*2.-1.);
    } else {

        O = texture(iChannel0, uv);

        vec2 pos = O.xy;
        vec2 vel = O.zw;

        // Replace vel with direction and constant speed later for boids
        // Keep vel for pbd physics :eyes:


        pos += vel*1e-3;

        pos.x -= floor(pos.x);
        pos.y -= floor(pos.y);

        O = vec4(pos, vel);

    }
}

// Voronoi buffer


void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    if (iFrame < 20){
        O = vec4(0);
        O.xy = uv;
        return;
    }


    O = texture(iChannel1, uv);
    // xy is data location of my particle

    vec2 myParticle = texture(iChannel0, O.xy/scale).xy;


    for (int i = 0; i < 8; i++){
        vec2 samp = mod(O.xy
            + (circleSample(uvec3(U, iDate.w*1000.))*2.-1.
            +  circleSample(uvec3(U, iDate.w*2000.))*2.-1.
            +  circleSample(uvec3(U, iDate.w*3000.))*2.-1.)*.001
        ,1.);

        vec2 newParticle = texture(iChannel0, samp/scale).xy;


        if (length(newParticle - uv) < length(myParticle - uv)){
            O = vec4(0);
            O.xy = samp;
        }
    }
}

const float scale = 10.;


const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}
//hash by IQ https://www.shadertoy.com/view/XlXcW4
vec2 circleSample(uvec3 x){
    vec2 v;
    for(v=hash(x+uint(v*1e9)).xy*2.-1.;
        dot(v,v)<1.;)
    return v;
}
