// Distance to a Cissoid curve
// (https://en.wikipedia.org/wiki/Cissoid_of_Diocles),
// solved through a quartic, minimizing the distance
// squared D² = (x-cx(t))²+(y-cy(t))² like in
// https://www.shadertoy.com/view/DdsyDX
// but all constants simplified and impossible root
// paths skipped. As with most quartic solvers, it's
// pretty unstable for large values of a, so not really
// useful currently.

float sdCissoid( in vec2 p, in float a )
{
    p.y = abs(p.y);

    float x = p.x/a;
    float y = p.y/a;
    float y2 = y*y;

    float t;

    #if 1
    // optional handling degenerate case x>0 && y==0
    if( x>0.0 && y==0.0 )
    {
        t = sqrt(sqrt(1.0+x)-1.0);
    }
    else
    #endif
    {
        float r = y2*(x-7.0)/64.0 - x/3.0 - 1.0/27.0;
        float s = y2/16.0 + x/3.0 - 1.0/9.0;
        float h = r*r + s*s*s;
        if( h<0.0 )
        {
            return length(p);
        }

        h = sqrt(h);
        vec2  k = vec2(h-r, -h-r);
        vec2  u = vec2(           pow(    k.x , 1.0/3.0),   // k.x>0 guaranteed
                        sign(k.y)*pow(abs(k.y), 1.0/3.0) );
        float m = u.x + u.y;

        #if 1
        // optional newtown-raphson (adapted from NinjaKoala's in https://www.shadertoy.com/view/DdsyDX)
        for( int i=0; i<2; i++ )
        {
            float m2 = m*m;
            m = (2.0/3.0)*(m2*m - r)/(m2 + s);
        }
        #endif

        float w = sqrt(y2/16.0 + 2.0*m - 4.0/3.0);
        float v = sqrt(y2/8.0  - 2.0*m - 8.0/3.0 + 2.0*y*(y2/64.0+1.0)/w);

        t = (w + v)/2.0 + y/8.0;
    }

    float z = 2.0*a*t*t/(1.0+t*t);
    vec2  q = vec2(z, z*t);

    float ss = sign( 2.0*y2 - x*(x*x+y2) );
    return ss*min(length(p),length(p-q));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // animation
    float ka = 0.5 + 0.4*cos(iTime*0.31+2.0);

    // distance
    float d = sdCissoid(p,ka);

    // coloring
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(128.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdCissoid(m,ka);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col, 1.0);
}
