float f_n_inoutexp(float n, float n_sharpness){
    n_sharpness = floor(n_sharpness) + mod(floor(n_sharpness),2.); // must be dividable by 2.
    n = max(0.,min(1., n));
    return min(pow(n, n_sharpness),pow( n-1., n_sharpness))*pow(2., n_sharpness);
}
