void mainImage( out vec4 fColor, in vec2 fCoord )
{
    vec2 uv = (2.*fCoord.xy - iResolution.xy)/iResolution.y;
    uv *= 2.;
    vec2 m = vec2(time*.1,-abs(sin(time/40.)*1.));
    m = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    vec3 c = vec3(0);

    /*vec3 camera = vec3(m*PI*2.,2);

    vec3 ca = vec3(
        atan(camera.z,camera.y),
        PI/2.+atan(camera.x,camera.z),
        0
    );*/
    // camera angle
    vec3 ca = vec3(
        PI/2.+m.y*PI/2.,
        PI/2.-m.x*PI/1.,
        0
    );

    float camz = PI;
    vec3 camera = vec3(0,0,camz)*rotX(ca.x)*rotY(ca.y);
    vec3 plane = vec3(uv,0)*rotX(ca.x)*rotY(ca.y);

    Ray r = initRay(camera,plane);

    sp[0].p = vec4(0,0,0,1);
    l[0].p = vec3(2,2,2);//*rotX(ca.x)*rotY(ca.y);
    l[1].p = vec3(-2,2,2)*rotX(ca.x)*rotY(ca.y);

    march(r,0);
    c += render(r);

    //c = r.p;
    fColor = vec4(c,1.0);
}

#define time iTime
#define resolution iResolution
#define mouse iMouse.xy
#define PI 3.14159265
#define MAX_DIST  100.
#define MIN_DIST  .001
#define MAX_STEP  128
#define MAX_REF   3

const int LIGHTS = 2;
const int SPHERES = 1;

struct Sphere {
    vec4 p;
    vec3 c;
},sp[SPHERES];

float sphereSDF(vec3 p,int id){
    return length(p-sp[id].p.xyz) - sp[id].p.w;
}
struct Ray {
	vec3 o;
	vec3 p;
	vec3 dir;
	vec3 c;
	float dist;
    float rl;
};

struct Light {
	vec3 p;
	vec3 c;
    vec3 L;
},l[LIGHTS];

vec3 N = vec3(0);
vec3 V = vec3(0);

float dist(vec3 p){
	float d = 0.;

	d = sphereSDF(p,0);

	return d;
}

float lightDist(vec3 p,float d){
    for(int i = 0;i<LIGHTS;i++)
        d = min(d,length(p - l[i].p)-.1);
	return d;
}

void march(inout Ray r,int t){
	for(int i = 0;i<MAX_STEP;i++){
		r.p = r.o + r.dist*r.dir;
		float d = dist(r.p);
		d = t == 0 ? d : min(d,lightDist(r.p,d));
		if(d <= MIN_DIST || d > MAX_DIST)break;
		r.dist += d;
	}
    r.rl += r.dist;
}

vec3 sNormal(vec3 p){
	vec2 t = vec2(.001,0);
	float d = dist(p);

	return normalize(d - vec3(
		dist(p-t.xyy),
		dist(p-t.yxy),
		dist(p-t.yyx)
	));
}

float diffuse(vec3 p,vec3 L){
	return max(dot(sNormal(p),normalize(L)),0.);
}

mat3 rotX(float a){
    float sa = sin(a),
        ca = cos(a);
    return mat3(
         1,  0, 0,
         0, sa,ca,
         0,-ca,sa
    );
}

mat3 rotY(float a){
    float sa = sin(a),
        ca = cos(a);
    return mat3(
        sa, 0,-ca,
         0, 1,  0,
        ca, 0, sa
    );
}

void lNormal(inout Light l,Ray r){
	l.L = normalize(l.p-r.p);
}

vec3 render(Ray r){
    vec3 c = vec3(0);
    N = sNormal(r.p);
    for( int i = 0;i< LIGHTS;i++){
        lNormal(l[i],r);
        c += max(dot(l[i].L,N),0.);
    }
    c *= .5+.5*N;

    if(r.dist > MAX_DIST)c = vec3(0);
    return c;
}

Ray initRay(vec3 c,vec3 p){
    // c camera
    // p projection plane
    Ray r;
    r.o = c;
    r.p = r.o;
    r.dir = normalize(p-c);
    r.c = vec3(0);
    r.dist = 0.;
    r.rl = 0.;
    return r;
}
