void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float horse = 0.0;

    // Body
    vec2 bodyPos = vec2(0.0, -0.2);
    float bodyRadius = 0.3;
    float body = 1.0 - smoothstep(bodyRadius, bodyRadius + 0.01, length(uv - bodyPos));

    // Head
    vec2 headPos = vec2(0.3, 0.1);
    float headRadius = 0.15;
    float head = 1.0 - smoothstep(headRadius, headRadius + 0.01, length(uv - headPos));

    // Legs
    float legRadius = 0.05;
    vec2 leg1Pos = vec2(-0.1, -0.65);
    vec2 leg2Pos = vec2(0.1, -0.65);
    float leg1 = 1.0 - smoothstep(legRadius, legRadius + 0.01, length(uv - leg1Pos));
    float leg2 = 1.0 - smoothstep(legRadius, legRadius + 0.01, length(uv - leg2Pos));

    // Tail
    vec2 tailPos = vec2(-0.3, -0.3);
    float tailRadius = 0.06;
    float tail = 1.0 - smoothstep(tailRadius, tailRadius + 0.01, length(uv - tailPos));

    horse = body + head + leg1 + leg2 + tail;
    fragColor = vec4(vec3(horse), 1.0);
}
