vec3 f_o_rgb_from_o_hsl(in vec3 c)
{
	vec3 rgb = clamp(abs(mod(c.x+vec3(0.,4.,2.),6.)-3.)-1.,0.,1.);
	return c.z+c.y*(rgb-0.5)*(1.-abs(2.*c.z-1.));
}

vec2 f_o_rotd(vec2 o, float n_ang){
    return vec2(
        cos(n_ang)*o.x - sin(n_ang)*o.y, 
        sin(n_ang)*o.x + cos(n_ang)*o.y
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor  = vec4(0.);

    float n_t = iTime*.5;

    float n_tau = 6.2831;
    vec2 o_trn_fc_nor = (fragCoord.xy -iResolution.xy*.5)/ iResolution.y;

    float b_a = float(fract(n_t* (1./2.) ) > .5);
    float t_linear_inout = 
        (1.-b_a) * fract(n_t)
        + b_a * (1.-fract(n_t));

    float b_f = float(t_linear_inout  < .5);
    float n_easing_inout_qubic = atan(t_linear_inout);

    // return x < 0.5 ? 4 * x * x * x : 1 - Math.pow(-2 * x + 2, 3) / 2;
    vec2 o_trn_p = o_trn_fc_nor * n_easing_inout_qubic*9.; 
    // (sin(n_t)*.5+.5)*5.+3.;
    vec2 o_trn_p_flr = floor(o_trn_p);
    vec2 o_trn_p_frc = fract(o_trn_p);
    vec2 o = (o_trn_p_frc-.5);
    // o = f_o_rotd(o, floor(o.x)*sin(n_t+o.x));
    vec2 o_abs = abs(o);
    float n_dcirc = length(o_abs);
    float n_dsqr = max(o_abs.x, o_abs.y);
    float n_dcntr = length(o_trn_fc_nor);
    float n_chng = abs(fract(n_dcntr+n_t)-.5)*9.;

    float n_c_linintrpl = n_chng;
    float n_shape = (1.-n_c_linintrpl)*n_dcirc + (n_c_linintrpl*n_dsqr);
    float n_radius = (sin(iTime)*.5+.5)*.5;
    n_radius = .25;
    n_shape = abs(n_shape-n_radius);
    n_shape = pow(n_shape, 1./2.);
    n_shape = 1.-n_shape;

    fragColor = vec4(
        n_shape
         * ((sin(
             vec3(
                sin(n_chng)*.5+.5,
                n_dcntr,
                (sin(dot(o_trn_p,o_trn_p))*.5+.5)
            ) + iTime
        )*.5+.5)+.5)
        + vec3(n_shape*1.+((sin(n_t+n_tau*.5)*.5+.5)*.2))*.01
        ,1.
    );
    // fragColor = vec4(n_chng);

    return;

}
