void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 data = texture( iChannel0, uv );    
    vec3 col = data.xyz/data.w;

	fragColor = vec4( pow(col, vec3(0.45)), 1.0 );
}

struct HitInfo {
    bool hit;
    float dst;
    vec3 point;
    vec3 normal;
    vec3 col;
    vec3 mat;
};

struct Ray {
    vec3 origin;
    vec3 dir;
};

uint base_hash(uvec2 p) {
    p = 1103515245U*((p >> 1U)^(p.yx));
    uint h32 = 1103515245U*((p.x)^(p.y>>3U));
    return h32^(h32 >> 16);
}

float g_seed = 0.;

vec2 hash2(inout float seed) {
    uint n = base_hash(floatBitsToUint(vec2(seed+=.1,seed+=.1)));
    uvec2 rz = uvec2(n, n*48271U);
    return vec2(rz.xy & uvec2(0x7fffffffU))/float(0x7fffffff);
}

vec3 hash3(inout float seed) {
    uint n = base_hash(floatBitsToUint(vec2(seed+=.1,seed+=.1)));
    uvec3 rz = uvec3(n, n*16807U, n*48271U);
    return vec3(rz & uvec3(0x7fffffffU))/float(0x7fffffff);
}


vec3 random_in_unit_sphere(inout float seed) {
    vec3 h = hash3(seed) * vec3(2.,6.28318530718,1.)-vec3(1,0,0);
    float phi = h.y;
    float r = pow(h.z, 1./3.);
	return r * vec3(sqrt(1.-h.x*h.x)*vec2(sin(phi),cos(phi)),h.x);
}

HitInfo RaySphere(Ray ray, vec3 center, float radius) {
    HitInfo hitInfo;
    hitInfo.hit = false;
    vec3 offsetRayOrigin = ray.origin - center;
    float a = dot(ray.dir, ray.dir);
    float b = 2. * dot(offsetRayOrigin, ray.dir);
    float c = dot(offsetRayOrigin, offsetRayOrigin) - (radius * radius);
    float disc = b*b-4.*a*c;
    float  dst = (-b - sqrt(disc)) / (2. * a);
    if (dst >= 0.) {
        hitInfo.hit = true;
        hitInfo.dst = dst;
        hitInfo.point = ray.origin + ray.dir * dst;
        hitInfo.normal = normalize(hitInfo.point - center);
    }
    return hitInfo;
}

mat3 rotate_x(float a){float sa = sin(a); float ca = cos(a); return mat3(vec3(1.,.0,.0),    vec3(.0,ca,sa),   vec3(.0,-sa,ca));}
mat3 rotate_y(float a){float sa = sin(a); float ca = cos(a); return mat3(vec3(ca,.0,sa),    vec3(.0,1.,.0),   vec3(-sa,.0,ca));}
mat3 rotate_z(float a){float sa = sin(a); float ca = cos(a); return mat3(vec3(ca,sa,.0),    vec3(-sa,ca,.0),  vec3(.0,.0,1.));}

#define sphere(pos,rad,Col,Mat) hitInfo = RaySphere(ray,pos,rad); if (hitInfo.hit && hitInfo.dst < closestHit.dst) { closestHit = hitInfo; closestHit.col = Col; closestHit.mat = Mat; }

void mainImage( out vec4 C, in vec2 fragCoord )
{
    g_seed = float(base_hash(floatBitsToUint(fragCoord)))/float(0xffffffffU)+iTime;
    vec2 i = (-iResolution.xy + 2.0*fragCoord + hash2(g_seed))/iResolution.y;
    vec3 viewPoint = vec3(i, 1);
    Ray ray;
    float rotx = 0.;
    float roty = 0.;
    ray.origin = vec3(0,0,2.);
    ray.dir = normalize(viewPoint - ray.origin);
    ray.dir *= rotate_x(roty);
    ray.dir *= rotate_y(rotx);
    vec3 lit = vec3(0.);
    vec3 col = vec3(1.);
    HitInfo closestHit;
    HitInfo hitInfo;
    for (int j = 0; j <= 8; j++) {
        closestHit.dst = 1e5;
        sphere(vec3(0,0,-110.),100.,vec3(1,1,1),vec3(0));
        sphere(vec3(0,-100,-10),100.,vec3(1,.4,0),vec3(0));
        sphere(vec3(0,110,-10),100.,vec3(1,1,1),vec3(0));
        sphere(vec3(-110,0,-10),100.,vec3(1,0,0),vec3(0));
        sphere(vec3(110,0,-10),100.,vec3(0,0,1),vec3(0));

        sphere(vec3(0,15,-4),6.,vec3(1. ,1.,1),vec3(1,0,0));

        sphere(vec3(0,0,0),.5,vec3(1. ,1.,1),vec3(0,.7,0));
        sphere(vec3(-1.2,0,0),.5,vec3(0,1,0),vec3(0));
        sphere(vec3(1.2,0,0),.5,vec3(0,0,1),vec3(0));
        if (closestHit.hit) {
            ray.origin = closestHit.point;
            vec3 dif = normalize(closestHit.normal + random_in_unit_sphere(g_seed));
            vec3 spec = reflect(ray.dir, closestHit.normal);
            ray.dir = mix(dif,spec,closestHit.mat.y);
            lit += col * closestHit.mat.x;
            col *= closestHit.col.xyz;
        } else break;
    }
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 data = texture( iChannel0, uv );
    C = data;
    if (iFrame == 0) C = vec4(0);
    else C += vec4(lit,1.);
}
