vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ;
    return
        abs(fract((a+a2)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 6; k++){
        uv =
            (uv+t2)
        ;
        t2 =
            -triangle_wave(uv-.5)
        ;
        uv =
            t2-triangle_wave(uv.yx)
        ;
        col.x =
            (length(uv+t2.yx)+uv.x)*2.;
        col =
            min(abs(abs(col-1.)-col),abs(abs(col-1.)-col.yzx));
    }
    fragColor = vec4(col,1.0);
}
