vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 6; k++){
        //if(uv.y>0.) uv.x += .5;
        //else uv.y += .5;

        uv =
            abs(.5+uv.yx+t2.yx)/1.5
        ;
        //uv += sign(uv-uv.yx)/2.+.5;
        if(uv.y>uv.x) uv = uv.yx;
        //else k += 1;

        t2 =
            -triangle_wave(uv.yx-.5)
        ;
        //if(t2.y>t2.x) t2 = t2.yx;
        uv =
            t2-triangle_wave(uv.yx)
        ;

        {t2 = t2.yx; uv=uv.yx;}


        col =
            abs(vec3(col.zx,max(abs(uv.x-uv.y)*sign(t2.x-t2.y),0.)))
        ;
        //uv /= 1.+mod(uv.y,2.);
    }
    fragColor = vec4(col*2.,1.0);
}

float fmod(float x,float y){
    return mod(floor(x),y);
}

vec2 mainSound(int samp, float t){
  float s1 =
      8.,
  m1 =
      0.,
  d1 = 1.;
  ;
  t /= 8.;
  for(int i = 0; i < 4; i++){
      m1 =
      fmod(t*d1+m1,s1+m1);
      float d2 = (1.+fmod(m1*t,2.));
      d1 /= d2;
      t /= d2;
  }
  t /= d1;
  t /=
      1.+fmod((t*s1*4.+floor(t*s1*4.)),2.)
  ;
  float m3 =
      fmod((t+floor(t*16.*2.+s1))/(m1+.5),s1)
  ;
  t *=
      1.+fmod(((t*s1)+floor(t*s1/2.+s1)),2.)
  ;

  float a= 64.*((1.-sqrt(fract(t*s1))))*.2,

  nb = pow(2.,(m3)/5.+7.5)*4.;

  return abs(.5-vec2(fract(t*nb*.998)*a
   ,fract(t*nb)*a));

}
