// ray casting bounding shape
const float CAM_DIST = 6.;
const float CAM_FLE = 1.3;
const float BBOX_RADIUS = 8.;
const float RAY_MIN_DIST = 0.;
const float RAY_MAX_DIST = CAM_DIST+1.1*BBOX_RADIUS;

// ray march
const int MAX_MARCHING_STEPS = 600; /*<1, 300, 2000>*/
float RAY_STEP_FACTOR3 = 0.27755; /*<0.05, 0.27755 ,1.0 */
const float RAY_PRECISION = 0.00001;

// hexaeder
const int PS_f = 6;
const int PS_e_f = 4;
const float PS_a0 = 8.;

// torus
const float J1_sh_a0 = -1.;
const float J1_sh_a1 = -0.4;
const int   J1_sh_f1 = 1*PS_e_f;
float J1_sh_pa1 = 0.03;
const int   J1_to_c1 = 12*PS_e_f;
const int   J1_to_f1 = 2*PS_e_f;
const float J1_to_pa1 = 0.0;

// texture
const int   TEX_MODE = 1; // 0: white, 1: stripes, 2: normal, 3: hex_cell_id
const bool  TEX_UV_ON = true;
const float TEX_UV_SHARPNESS = 0.00000001;
const vec2  TEX_UV_SUBDIV = vec2(1*PS_e_f, 1*PS_e_f);

// shade
const bool SHADE_ON = true;

// fog
const bool  FOG_ON = true;
const float FOG_DENSITY = 0.001;

float map(in vec3 p, out vec4 mat) {   
    //// sdf's
    float rxy = 0., au = 0.0, av = 0.0;
    float cell_id = 0., sr = 0.; //sig(sr): sens of rotation, abs(sr) : number edges
    vec2 z = vec2(0.);

    // stereographic 4D, rotation
    p = rotz4D( p, iTime/20., sqrt(abs(PS_a0)));
    float a_xy = 2.*PI*iTime/30.;
    p.xy = cmul(p.xy, vec2(cos(a_xy),sin(a_xy)));

    //// map platonic solid cells to one cell
    vec3 pn = normalize(p); 

    // projection from n cells to 1 cell. 
    pn = hexa_cells(pn, cell_id, sr);
    z = pn.xy/(1.-pn.z);
    z = cHexaPolyArc2disk(z);

    /// r-polygon-distribution
    vec2 e = vec2(sqrt(2.), 0.5);
    float en = 1.+( (PS_a0<0.?e.x:e.y)-1. )*smoothstep(1.,5., abs(PS_a0));        
    float r = length(z); vec2 zn = z/r;
    z = dist(r, en) * zn;   

    /// stereograpic projection    
    pn = sp(vec3(z,sr));

    /// scale radius
    p = length(p) * pn;

    ////fractal level 0: Torus
    au = atan(p.y, p.x);
    rxy = length(p.xy); 
    z = vec2(rxy, p.z);
    z = cmul(z,z); z.x -= PS_a0;

    ////fractal level 1: 2Djulia realaxis-translation,fraction,rotation
    float sh1 = J1_sh_a0;
    float to1 = au*float(J1_to_c1)/float(J1_to_f1);
    sh1 += J1_sh_a1*(1.-pulsecollision(float(J1_sh_f1),au/PI, J1_sh_pa1, 0.5));
    to1 += J1_to_pa1;  
    z = crpt(z, to1, float(J1_to_f1), sh1);     

    // distance estimation shape
    float d = log(length(z));

    #if 0
    st_assert( d != INF, 0 ); // Shows the +INF singularity as a sphere (floating point precision).
    #else
    if (d == INF) {d = 1.;} // step through the singularity
    #endif

    mat.x = cell_id; mat.yz = z; mat.w = au *float(J1_sh_f1); // object id, complex plane metatorus, angle-u metatorus
    return d;
}

float intersect(in vec3 ro, in vec3 rd, out vec4 rescol, in float px) {
    float res = -1.0; // init no intersection

    // bounding shape BB
    vec2 dis = isphere( ro, rd , abs(BBOX_RADIUS));

    if( dis.y<0.0 ) return -1.0; // check BB behind came

    dis.x = max( dis.x, RAY_MIN_DIST ); // check min ray length
    dis.y = min( dis.y, RAY_MAX_DIST ); // check max ray length

    // raymarch signed distance field
    vec4 trap; // data from surface intersection point

	float fh = RAY_STEP_FACTOR3;
    float t = dis.x; // ray starts at boundingbox
    for( int i=0; i<MAX_MARCHING_STEPS; i++  ) // max steps
    { 
        vec3 pos = ro + rd*t;
        float th = RAY_PRECISION * px * t; //th = 0.0001; // minimal bound surface hit
        float h = map( pos, trap );
        if( t>dis.y || h<th ) break; // ray outside BB or hit.
        t += h*fh; // distance
    }
    if( t<dis.y ) // ray inside BB
    {
        rescol = trap; // save data
        res = t; // save ray length
    }
    return res;
}

vec3 calcNormal( in vec3 p, in float prec ) {
    vec4 tmp; // dummie
    vec2 e = vec2( prec, 0. );
    return normalize(vec3(
		map(p+e.xyy,tmp) - map(p-e.xyy,tmp),
		map(p+e.yxy,tmp) - map(p-e.yxy,tmp),
		map(p+e.yyx,tmp) - map(p-e.yyx,tmp) )
    	);
}

vec3 transform(in vec3 p, in float time) {
	// camera transformation
	if (iMouse.x > 0.0)
	{
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
		p.yz = crot(p.yz,-PI*m.y);
		p.zx = crot(p.zx,-PI*m.x);
	}
	//p.xz = crot(p.xz,time*0.1);
	return p.xyz;
}

vec3 phong(in vec3 lightdir, in vec3 raydir, in vec3 surfnormal) {
  //cubemap
  //vec3 K_a = 1.5 * vec3(0.0,0.5,0.8) * texture(iChannel0, reflect(rd, normal)).rgb;
  // ratio reflection, light color
  vec3 K_a = 0.25 * vec3(0.7, 0.7, 0); //ambient light
  vec3 K_d = 0.5  * vec3(0.7, 0.5, 0); //diffuse light
  vec3 K_s = 0.25 * vec3(1); float alpha = 10.;// specular light

  float diffuse = clamp(dot(lightdir, surfnormal), 0., 1.);
  float specular = pow(clamp(dot(reflect(lightdir, surfnormal), -raydir), 0., 1.), alpha);
  // ambient- diffuse- specular- light reflection
  return K_a + K_d * diffuse + K_s * specular;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // slider mapping
    RAY_STEP_FACTOR3 = (0.5-0.0001)* pow(RAY_STEP_FACTOR3, 3.) + 0.0001;

    // time modulation
    J1_sh_pa1 *= iTime;

    // normalized coords
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    // camera viewport --> worldspace
    vec3 ro = vec3(0,0,-CAM_DIST);
    vec3 rd = vec3(-uv.x,uv.y,CAM_FLE);
    	 ro = transform(ro,iTime);
	     rd = normalize(transform(rd,iTime));

    // ray
    vec4 mat = vec4(0.0); // material-data: vec4(float cell_id, vec2 complex plane, float angle-u)
    float px = 2.0/( iResolution.y*CAM_FLE );
    float t = intersect( ro, rd, mat, px ); // distance hit ray surface

    // color
    // background-color
    vec3 bg_col = smoothstep(vec3(1.000,0.945,0.678),vec3(1.000,0.749,0.459),vec3(0.5,rd.y,0.6));
         bg_col *= srgb2rgb( texture(iChannel0, vec3(-rd.x,rd.yz)).rgb );
    vec3 bg_hsv = rgb2hsv(bg_col);
         bg_hsv.x +=0.01*iTime;
         bg_col = hsv2rgb(bg_hsv.x,0.8* bg_hsv.y,1.5*bg_hsv.z);

    vec3 col = vec3(1,1,0); // init, test-color
    if (t < 0.0) /* no hit -> sky */ {
        col = bg_col;
    }
    else /* hit -> object, surface */ {
    	// surface properties
        vec3 p = ro + rd * t;
        vec3 n = calcNormal( p, 0.001);        
        // object id
        float obj_id = mat.x;
        // uv-coords
        vec2 mat_uv;
        mat_uv.x = 0.5*mat.w/PI;
        mat_uv.y = atan(mat.z,mat.y)/PI;
        // uv-texture
        col = (TEX_MODE == 1) ? h2rgb(abs(mat_uv.y))      // stripes
            : (TEX_MODE == 2) ? 0.6*n+vec3(0.4)           // normal
            : (TEX_MODE == 3) ? h2rgb(obj_id/float(PS_f)) // hex_cell_id
            : vec3(1.);
        // uv-grid (TODO: not orthogonal!)
        if(TEX_UV_ON) {
            mat_uv *= PI*TEX_UV_SUBDIV;
            col *= 0.5+ 0.5*smoothstep(-TEX_UV_SHARPNESS,TEX_UV_SHARPNESS, sin(mat_uv.x)*sin(mat_uv.y) );
        }        
        // lighting
        if (SHADE_ON) {
            // point_light
            vec3 point_lo = vec3(-8, -6, -5);
            vec3 point_ld = normalize(point_lo - p);
            float point_li = 0.6;
            // came_light
            vec3 came_lo = transform(vec3(0.,0.,-CAM_DIST),iTime); // light origine
            vec3 came_ld = normalize(came_lo - p); // light direction
            float came_li = 0.4; // light intensity
            // Phong
            col *= 0.6;
            col += 0.2 * phong(point_ld, n , rd);
            col += 0.2 * phong(came_ld, n, rd);
            // fresnel
            col += pow(clamp(1. - dot(n, -rd), 0., 1.), 5.) * 0.4 * bg_col;
        }
    }
    // fog
    if(FOG_ON) col = mix(col, bg_col, 1.0 - exp(-FOG_DENSITY * pow(t,3.)));
    // gamma correction
    fragColor = vec4(rgb2srgb(col),1.);
}

// consts
const float PI = abs(atan(0.,-1.));
const float INF = abs(1./0.);
const float NAN = abs(0./0.);

/// 1D transformations
float pulsecollision(in float pf, in float x, in float x0, in float pw) {
    /*
    x  : periodic in [0,1]
    x0 : offset
    pw : pulse width in [0,1]
    pf : pulse frequency 
    */
    float ntriw_p = abs( fract( pf*(x - x0) )-0.5); // x_normalized triangle wave function moving in +x_direction
    float ntriw_n = abs( fract( pf*(x + x0) )-0.5); // x_normalized triangle wave function moving in -x_direction
    float pp = smoothstep( 0.5*pw, 0., ntriw_p );   // pulse moves in +x_direction
    float pn = smoothstep( 0.5*pw, 0., ntriw_n );   // pulse moves in -x_direction
    return (pp+pn);                                 // smooth superposition
}

/// 2D transformations
/// complex
vec2 cmul(vec2 za,vec2 zb){
    return za*mat2(zb.x,-zb.y,zb.yx);}

vec2 crot(vec2 z,float a){
    return cmul(vec2(cos(a),sin(a)),z);}

vec2 cinv(vec2 z){
    return z*vec2(1,-1)/dot(z,z);}

vec2 cdiv(vec2 z, vec2 w){
    return cmul(z,cinv(w));}

vec2 cpow(vec2 z, float q){
    float r=pow(length(z), q);
	float a=q*atan(z.y,z.x);
    return vec2(r*cos(a),r*sin(a));}

vec2 cpow(vec2 z, int n){
  float r = length(z);
  float theta = atan(z.y,z.x);
  return pow(r,float(n))*normalize(vec2(cos(float(n)*theta),sin(float(n)*theta)));}

vec2 crpt(vec2 z, float a, float p, float x0){
  /* (z*e^ia)^p-x0 */
  return cpow( cmul(z, vec2(cos(-a),sin(-a))),p) - vec2(x0, 0.);}

vec2 cHexaPolyArc2disk(vec2 z) {
    // https://people.maths.ox.ac.uk/trefethen/lightning.html
    const int NUM_j = 18; // poles
    const vec2[] rf = vec2[NUM_j]
    (vec2( 0.7072, -0.7070)
    ,vec2(-0.7070,  0.7072)
    ,vec2(-0.0377, -0.9993)
    ,vec2( 0.7072,  0.7070)
    ,vec2(-0.7072, -0.7070)
    ,vec2(-0.0377,  0.9993)
    ,vec2( 0.5452, -0.8383)
    ,vec2( 0.9936,  0.1126)
    ,vec2(-0.9936,  0.1126)
    ,vec2(-0.7983, -0.6022)
    ,vec2(-0.7563,  0.6542)
    ,vec2( 0.6542,  0.7563)
    ,vec2( 0.7563, -0.6542)
    ,vec2(-0.6235,  0.7818)
    ,vec2(-0.7167, -0.6974)
    ,vec2(-0.6542, -0.7563)
    ,vec2( 0.7342,  0.6790)
    ,vec2( 0.7025, -0.7117));

    const vec2[] rw = vec2[NUM_j]
    (vec2( 0.0135,  0.0   )
    ,vec2( 0.0008,  0.0001)
    ,vec2(-0.5762,  0.1282)
    ,vec2( 0.0013,  0.0002)
    ,vec2(-0.0135, -0.0085)
    ,vec2( 0.1239, -0.0601)
    ,vec2(-0.2162, -0.3769)
    ,vec2( 0.1197, -0.1327)
    ,vec2( 0.2544,  0.0708)
    ,vec2( 0.3228,  0.3050)
    ,vec2( 0.0377, -0.0056)
    ,vec2( 0.0362, -0.0135)
    ,vec2( 0.1439, -0.0871)
    ,vec2( 0.0551,  0.0066)
    ,vec2(-0.0935,  0.1492)
    ,vec2(-0.2397,  0.1216)
    ,vec2( 0.0218, -0.0211)
    ,vec2( 0.0077, -0.0763));

    const vec2[] rz = vec2[NUM_j]
    (vec2( 0.366242, -0.365216)
    ,vec2(-0.365216,  0.366242)
    ,vec2(-0.012767, -0.414156)
    ,vec2( 0.366242,  0.365216)
    ,vec2(-0.366242, -0.365216)
    ,vec2(-0.012767,  0.414156)
    ,vec2( 0.216186, -0.397592)
    ,vec2( 0.413695,  0.038296)
    ,vec2(-0.413695,  0.038296)
    ,vec2(-0.391705, -0.251311)
    ,vec2(-0.383838,  0.291533)
    ,vec2( 0.291533,  0.383838)
    ,vec2( 0.383838, -0.291533)
    ,vec2(-0.266502,  0.388876)
    ,vec2(-0.372411, -0.341303)
    ,vec2(-0.291533, -0.383838)
    ,vec2( 0.378349,  0.316470)
    ,vec2( 0.350877, -0.369995));

    // polynomial barycentric representation
    vec2 zn = vec2(0.); vec2 zd = vec2(0.);       
    for(int j = 0; j<NUM_j; j++) {
        vec2 zj = cdiv( rw[j], (z-rz[j]));
        zn += cmul(rf[j], zj); // sum nominator
        zd += zj;              // sum denominator
    }
    return cdiv(zn, zd);
}

/// 4D transformation
vec3 rotz4D(in vec3 p, in float t, in float r_inv){
    float a = 2.*PI*t;
    p /= r_inv;
    float rn = length(p);
    float rn2 = dot(rn,rn);
    vec2 zw = cmul(vec2(2.*p.z, rn2-1.),vec2(cos(a),sin(a)));
    p = vec3(2.*p.xy,zw.x)/(rn2+1.-zw.y);   
    return r_inv * p;
}

// map spherical platonic solid pyramidal-face-cells to one cell located at -z-axis.
vec3 hexa_cells(in vec3 p, out float cell_id, out float sr){
	//// hexagon
    // cells +X, -X, +Y, -Y,+Z, -Z
    // cell walls defined by the face_normals
    bool nBA = dot(vec3( 1., 0.,-1.),p)>=0.;
	bool nCA = dot(vec3( 0., 1.,-1.),p)>=0.;
	bool nDA = dot(vec3( 1., 1., 0.),p)>=0.;
	bool nCB = dot(vec3(-1., 1., 0.),p)>=0.;
	bool nDB = dot(vec3( 0., 1., 1.),p)>=0.;
	bool nDC = dot(vec3( 1., 0., 1.),p)>=0.;
    // map (+X, -X, +Y, -Y,+Z, -Z)-cells to (-Z)-cell by rotation
    // rotation angles
    const float a90 = PI/2.; // a = 90°       
    const vec2 ei_a0   = vec2(1., 0.);             // a = 0° 
    const vec2 ei_a90  = vec2(cos(a90), sin(a90)); // a = 90°

    vec2 ei_y; vec2 ei_x;
	//inside cell +X: +X --> -Z
	if(nBA && nDA && !nCB && nDC) {
		cell_id = 0.;
		ei_y = ei_a90;
		ei_x = ei_a0;
	}
	//inside cell -X: -X --> -Z
	else if(!nBA && !nDA && nCB && !nDC) {
		cell_id = 1.;
		ei_y = -ei_a90;
		ei_x = ei_a0;
	}
    //inside cell +Y: +Y --> -Z
	else if(nCA && nDA && nCB && nDB) {
		cell_id = 2.;
		ei_y = -ei_a90;
		ei_x = -ei_a90;
	}
	//inside cell -Y: -Y --> -Z
	else if(!nCA && !nDA && !nCB && !nDB ) {
		cell_id = 3.;
		ei_y = ei_a90;
		ei_x = ei_a90;
	}
	//inside cell +Z: +Z --> -Z
	else if(!nBA && !nCA && nDB && nDC) {
		cell_id = 4.;
		ei_y = -ei_a0; // 180°
		ei_x = ei_a0;
	}
	//inside cell -Z: -Z --> -Z
	else if(nBA && nCA && !nDB && !nDC) {
		cell_id = 5.;
		ei_y = ei_a0;
		ei_x = ei_a0;
	}        
	// cell orientation
	p.zx = cmul(p.zx,ei_y); // rotate around y-axis
	p.yz = cmul(p.yz,ei_x); // rotate around x-axis
	// non cyclic
	sr = 4.;
    return p;
}

// stereographic projection 
float sig(float x) {
    return x < 0. ? -1. : +1.; 
    }

float dist(float x, float e) {
    // e in [0., +inf]
    return sign(x)*abs( pow(sign(x)*x, abs(e)) );
    //return sign(x)*abs( pow(abs(x), abs(e)) );
    }

vec2 r_poly_dist(vec2 z, vec2 e, float sh_a) {   
    /// r-polygon-distribution
    float r = length(z); z = (r<0.)? vec2(0) : z/r; 
    float d0 = 0.5 *abs(e.y-e.x);
    float e2 = e.x + d0 + d0 * (2.*smoothstep(-1., 1., -sh_a) - 1.);
    return dist(r, e2) * z;
}   

vec3 sp(vec3 zsr){
    vec2 z = zsr.xy; float sr = zsr.z;
    // stereopraphic projection (complex plane to unit sphere)
    float zz = dot(z,z); vec3 pn;
    if (bool(sig(sr))) {// north pole
        pn = vec3(2. * z.x,  2. * z.y, -1. + zz);
        }
    else { // south pol
        z = cinv(z);
        pn = vec3(2. * z.x, -2. * z.y,  1. - zz);
        }
    pn /= 1.+zz;
    return pn;
}

vec3 spb(vec3 zsr) {
    vec2 z = zsr.xy; float sr = zsr.z;
    // inverse stereograpic projection
    float k = 2./(1.+dot(z,z)); vec3 pn;
    if (bool(sig(sr))) {// north pole
        pn = vec3(k*z, k-1.); }
    else { // south pol
        z = cinv(z);
        pn = vec3(k*z, -(k-1.)); }
    return pn;
}

// bb
vec2 isphere(in vec3 ro, in vec3 rd, in float r) {
    // https://iquilezles.org/
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;   
    if( h<0.0 ) return vec2(-1.0);
    h = sqrt( h );
    return -b + vec2(-h,h);
}

// colorspace transformations
vec3 rgb2hsv(vec3 col) {
    // https://en.wikipedia.org/wiki/HSL_and_HSV
    col = clamp(col, 0., 1.); 
    //
    float m_max = max(col.r,max(col.g,col.b));
    float m_min = min(col.r,min(col.g,col.b));
    float d = m_max-m_min;
    // hue
    float h = 0.; // if(d == 0.) undefined
    if(m_max==col.r) {h = (col.g-col.b)/d+0.;}
    if(m_max==col.g) {h = (col.b-col.r)/d+2.;}
    if(m_max==col.b) {h = (col.r-col.g)/d+4.;}
    h = fract(h/6.); // periodic in[0.,1.]
    // value
    float v = m_max;
    // saturation
    float s = (v == 0.)?0.: d/v;
    return vec3(h,s,v);
}

vec3 h2rgb(float h){
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return rgb*rgb*(3.0-2.0*rgb); /* cubic smoothing */
}

vec3 hsv2rgb(float h, float s, float v){     
    return v * mix( vec3(1.0), h2rgb(h), s); /* hue, saturation, value */
}

// srgb <--> rgb (linear)
vec3 srgb2rgb(vec3 col) {
    return pow(col, vec3(2.2));
}

vec3 rgb2srgb(vec3 col) {
    return pow(col, vec3(1./2.2));
}
