#define a(a,b,f,o) mix(a,b,sin(o+e*f)*.5+.5)
#define s(a) mat2(cos(a+vec4(0,33,11,0)))

void mainImage(out vec4 O, vec2 F) {
    float c , 
          o ,
          complex = 30.,
          e = 1.4+11.962+(iTime+ 59.2)*0.001111;
          if (e > 13.43) {e =+ iTime*.111111 + 11.54;complex += .1*iTime;}

    vec2  r = iResolution.xy; 

    for (;o++<complex;) {
        vec3 p = abs(.7*c*normalize(vec3((F+F-r)/r.y, 1)));
        p.xy *= s(e);
        p.zy += e + c*tan(2.*e);
        p = fract(p)-.5;
        p.xy *= s(c);
        p.xz *= s(e);
        p.y = max(abs(p.y)-a(0.,.2,1.,),0.);
        c += (length(vec2(length(p.xy)-.2,p.z)) 
                 -a(.4,.1,.5,4.) - c*.01)*.5;
    }

    O.rgb = 1.2*(cos(c*110. + .99*vec3(0,1.+c*.2,2))+.2)/exp(c*0.0914);
}
