#define PI 3.14159265359
vec2 RotateUV(inout vec2 uv, float angle)
{	
	angle = angle * PI / 180.0;
    return uv * mat2(cos(angle),sin(angle),-sin(angle),cos(angle));
}
vec3 Color255(int R, int G, int B)
{
    return vec3(float(R)/255.0, float(G)/255.0, float(B)/255.0);
}
float Clamp01( in float value)
{
    return clamp(value,0.0,1.0);
}

// signed distance of a 2D EquilateralTriangle from IQ
//(https://www.shadertoy.com/view/Xl2yDW)
float sdEquilateralTriangle(  in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p=vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}
float sdBox( in vec2 pos, in vec2 box )
{
    vec2 dist = abs(pos)-box;
    return length(max(dist,0.0)) + min(max(dist.x,dist.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    // UV manipulation
    vec2 uv = fragCoord.xy - 0.5 * iResolution.xy;
	uv = 2.0 * uv.xy / iResolution.y;

    float t = mod(iTime*0.5,5.0);
    t = abs(sin(iTime*0.25)* 4.)-.5;

    //Anim
    float angle = sin(-iTime) * 6.66 * mix(0.0,1.0,Clamp01(t-2.0));
    uv = RotateUV(uv, angle);
    //Height Offset
    uv.x += sin(-iTime) * 0.1 * mix(0.0,1.0,Clamp01(t-2.0));
    uv.y += cos(-iTime) * 0.1 * mix(0.0,1.0,Clamp01(t-2.0));

    //Parameter
    vec2 uvL = RotateUV(uv,-120.0);
    vec2 uvR = RotateUV(uv, 120.0);

    float dist = 0.15;
    float size = 0.6;
    float borderSize = 0.1;
    float rounding = 0.1;
    float blurLvl = 100.0;

    //Shapes
    float triA = sdEquilateralTriangle(uv  + vec2(0.0,dist), mix(-dist,size,Clamp01(t)));
    float triB = sdEquilateralTriangle(uvL + vec2(0.0,dist), mix(-dist,size,Clamp01(t)));
    float triC = sdEquilateralTriangle(uvR + vec2(0.0,dist), mix(-dist,size,Clamp01(t)));

    float rectA = sdBox(uv  + vec2(0.0,-size-dist+borderSize-0.02), vec2(mix(-1.0,borderSize,Clamp01(t-.33))));
    float rectB = sdBox(uvL + vec2(0.0,-size-dist+borderSize-0.02), vec2(mix(-1.0,borderSize,Clamp01(t-.44))));
    float rectC = sdBox(uvR + vec2(0.0,-size-dist+borderSize-0.02), vec2(mix(-1.0,borderSize,Clamp01(t-.55))));

    float diagMask = sdBox(uvR + vec2(0.0,0.18), vec2(1.0,mix(-1.0,0.025,Clamp01((t*.33) +.25))));
    float lineMask = sdBox(uv + vec2(0.3,0.18), vec2(0.55,mix(-1.0,0.025,Clamp01((t*.33) +.1))));

    // color
    vec3 bgCol = vec3(.0902);
    vec3 logoCol = Color255(19,175,240);

    vec3 pixel = bgCol; 

    pixel = mix(logoCol,pixel, Clamp01((triA-rounding)*blurLvl));
    pixel = mix(logoCol,pixel, Clamp01((triB-rounding)*blurLvl));
    pixel = mix(logoCol,pixel, Clamp01((triC-rounding)*blurLvl));

    pixel = mix(logoCol,pixel, Clamp01((rectA-rounding)*blurLvl));
    pixel = mix(logoCol,pixel, Clamp01((rectB-rounding)*blurLvl));
    pixel = mix(logoCol,pixel, Clamp01((rectC-rounding)*blurLvl));

    pixel = mix(bgCol,pixel, Clamp01((diagMask-0.1)*blurLvl));
    pixel = mix(bgCol,pixel, Clamp01((lineMask-0.1)*blurLvl));

	vec4 result = vec4(pixel, 1.0);
	fragColor = result;
}
