//multiply by prime numbers to get an interesting rhythm
float fmod(float a, float b){



    /*
    for(float i = 0.; i < mod(floor(a),8.); i++){
        a += mod(floor(a/8.),8.)*i;
    }
    */

    //divide by powers of 2 and multiply by prime numbers to get interesting rhythms
    a += mod1(floor((a)/128.)*19.,128.);
    a += mod1(floor((a)/64.)*17.,64.);
    a += mod1(floor((a)/32.)*13.,32.);
    a += mod1(floor((a)/16.)*7.,16.);
    a += mod1(floor((a)/8.)*5.,8.);
    a += mod1(floor((a)/4.)*3.,4.);
    a += mod1(floor((a)/2.)*2.,2.);

    //a += mod(floor((a)/2.)*13.,2.);
    //a += mod(floor((a)/2.)*17.,2.);
    return mod1(floor(a),b);
}

