vec4 pal(in vec4 i, in vec2 uv) {
    // Convert saturated (ie., approximately binary) xyzw values
    // into a 16-colour palette.  In this case, an approximation
    // of the C64 colour palette.  Why the C64?  I do not know.
    // I just wanted to do away with that RGB-filter look that
    // comes out otherwise.
    vec4 j = vec4(1.) - i;
#if 0
    return (j.x * j.y * j.z * j.w) * vec4(0., 0., 0., 0.)
         + (j.x * j.y * j.z * i.w) * vec4(1., 1., 1., 0.)
         + (j.x * j.y * i.z * j.w) * vec4(0., 1., 0., 0.)
         + (j.x * j.y * i.z * i.w) * vec4(.5, 0., 0., 0.)
         + (j.x * i.y * j.z * j.w) * vec4(.6, 1., .9, 0.)
         + (j.x * i.y * j.z * i.w) * vec4(.7, .3, .7, 0.)
         + (j.x * i.y * i.z * j.w) * vec4(0., .7, .4, 0.)
         + (j.x * i.y * i.z * i.w) * vec4(0., 0., .6, 0.)
         + (i.x * j.y * j.z * j.w) * vec4(.9, .9, .5, 0.)
         + (i.x * j.y * j.z * i.w) * vec4(.8, .5, .3, 0.)
         + (i.x * j.y * i.z * j.w) * vec4(1., .5, .5, 0.)
         + (i.x * j.y * i.z * i.w) * vec4(.2, .2, .2, 0.)
         + (i.x * i.y * j.z * j.w) * vec4(.5, .5, .5, 0.)
         + (i.x * i.y * j.z * i.w) * vec4(.6, 1., .7, 0.)
         + (i.x * i.y * i.z * j.w) * vec4(0., .5, 1., 0.)
         + (i.x * i.y * i.z * i.w) * vec4(.8, .8, .8, 0.);
#else
    return (j.x * j.y * j.z * j.w) * vec4(0., 0., 0., 0.)
         + (j.x * j.y * j.z * i.w) * vec4(1., 1., 1., 0.)
         + (j.x * j.y * i.z * j.w) * vec4(0., 1., 0., 0.)
         + (j.x * j.y * i.z * i.w) * vec4(.5, 0., 0., 0.)
         + (j.x * i.y * j.z * j.w) * vec4(.6, 1., .9, 0.)
         + (j.x * i.y * j.z * i.w) * vec4(.7, .3, .7, 0.)
         + (j.x * i.y * i.z * j.w) * vec4(0., .7, .4, 0.)
         + (j.x * i.y * i.z * i.w) * texture(iChannel3, uv + vec2(0.01, 0.03) * iTime)
         + (i.x * j.y * j.z * j.w) * vec4(.9, .9, .5, 0.)
         + (i.x * j.y * j.z * i.w) * vec4(.8, .5, .3, 0.)
         + (i.x * j.y * i.z * j.w) * vec4(1., .5, .5, 0.)
         + (i.x * j.y * i.z * i.w) * vec4(.2, .2, .2, 0.)
         + (i.x * i.y * j.z * j.w) * vec4(.5, .5, .5, 0.)
         + (i.x * i.y * j.z * i.w) * vec4(.6, 1., .7, 0.)
         + (i.x * i.y * i.z * j.w) * vec4(0., .5, 1., 0.)
         + (i.x * i.y * i.z * i.w) * texture(iChannel2, uv + vec2(0.02, -0.007) * iTime);
#endif

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 m = iMouse.xy / iResolution.xy;
    vec4 b = texture(iChannel0, uv);
    vec4 c = texture(iChannel1, uv);
    if (uv.x > 0.95) b = texture(iChannel1, uv, 100.5);
    b = m.y > uv.y ? b : c;
    b = pal(b, uv);
    fragColor = b;
}

// Mostly a generic framebuffer feedback thing, with some
// scribbling over the top.
// To help edge towards rounded shapes, the smoothed and
// sharpened buffer is fed back into this as well, so that
// it gradually sharpens itself as it mixes and the shapes
// should become progressively rounder from frame to frame
// (if not for the stirring).


vec4 funborder(sampler2D s, vec2 uv) {
    if (any(notEqual(uv, fract(uv)))) {
        uv -= 0.5;
        float th = atan(uv.y, uv.x) + iTime;
        return max(vec4(0), sin(vec4(0.4, -0.7, -0.9, 0.1) * th) - 0.7);
    }
    return texture(s, uv);
}

// Just some random perturbation of the coordinates to make things swirl.
vec2 f(in vec2 uv) {
    const vec2 centre = vec2(0.35, 0.35);
    const float th = 0.003;
    const float r = 0.9995;
    float c = cos(th), s = sin(th);
    uv = (uv - centre) * mat2(c, s, -s, c) * r + centre;
    vec2 wiggle = vec2(sin(iTime + uv.y * 15.1), cos(uv.x * 14.1));
    uv += wiggle * 0.002;

    uv = (uv - 0.5) * 1.001 + 0.5;

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iChannelResolution[0].xy;
    vec2 vu = uv.yx;
    uv = f(uv);
    vu = f(vu).yx;
    vec4 q = 0.5 * vec4(funborder(iChannel1, uv).xz,
                        funborder(iChannel1, vu).yw).xzyw;
    vec4 col = vec4(funborder(iChannel0, uv).xz,
                    funborder(iChannel0, vu).yw).xzyw;

    // Magic number here seems like viscosity:
    col = mix(col, q, 0.078);

    // Try to enlarge colour fringes a bit.
    col *= vec4(1.005, 0.995, 0.995, 1.005);

    fragColor = col;
}

const int blur_taps = 15;
const float blur_radius = 8.0;

float aastep(float threshold, float f) {
    float margin = clamp(fwidth(f) * 0.7, 0.0, 0.1);
    return smoothstep(threshold - margin, threshold + margin, f);
}

vec4 aastep(float threshold, vec4 f) {
    vec4 margin = clamp(fwidth(f) * 0.7, 0.0, 0.1);
    return smoothstep(threshold - margin, threshold + margin, f);
}


float pdf(float x) {
#if 0
    // pdf for s=0.1667: `sum(uniform(-.5,.5, 3)/3)`
    return 2.39365*exp(-18. * x * x);
#else
    // pdf for s=0.144: `sum(uniform(-.5,.5, 4)/4)`
    return 2.77043 * exp(-24.1126 * x * x);
#endif
}


vec4 gsample(sampler2D tex, vec2 uv, vec2 v, float radius) {
#if 1
    vec4 sum = vec4(0);
    float den = 0.0;

    for (int i=-blur_taps; i <= blur_taps; i++) {
        float f = float(i) / float(blur_taps);
        float w = pdf(f / radius);
        vec2 ofs = v * f;
        sum += textureLod(tex, uv + ofs, 2.5) * w;
        den += w;
    }
    return sum / den;
#else
    return texture(tex, uv);
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = gsample(iChannel0, uv, vec2(0.05, 0.0), blur_radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = gsample(iChannel0, uv, vec2(0.0, 0.05), blur_radius);
}

// Read a blurred Buffer A (blurring aims to represent surface tension),
// and quantise it according to the average of the previous frame.
// The idea is that the average of the result should seek the median
// weight of Buffer A so that half the buffer is lit.
// This is fed back into buffer A to encourage it to gradually gain
// more defined edges and the expected weight, while still being able
// to move and blur.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    // Sample the main framebuffer with a whole lot of smoothing.
    vec4 colour = 0.50 * texture(iChannel0, uv);

#if 0
    // Take the average of the whole of the previous output;
    // if this is low then too few pixels were 'set', and
    // if it is high then too many pixels were 'set'.
    vec4 threshold = textureLod(iChannel1, vec2(0.5), 99.9);
#else
    // Take the average of the whole of the current input;
    // This divides the output into above and below average,
    // which is not the same as the median, but I think it will
    // seek the median more stably than above.
    vec4 threshold = textureLod(iChannel0, vec2(0.5), 99.9);
#endif

    // I didn't really think this through.  We need to make an informed
    // guess at where the correct threshold should be, but it's fiddly
    // trying to be stateful about previous guesses, so I just mashed
    // the keyboard until the flashing and blinking didn't happened so
    // much.
    threshold = mix(threshold, vec4(0.55), 0.1);
    colour = aastep(0.0, colour - threshold);

    fragColor = colour;
}

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    fragColor = vec4(0.0);
    vec2 clip = vec2(rayDir.z * 0.25);
    vec2 clipuv = abs(rayDir.xy);
    if (all(lessThanEqual(clipuv, rayDir.zz))
        && all(lessThanEqual(clipuv, clip))) {
        vec2 uv = rayDir.xy / clip;
        fragColor = texture(iChannel0, uv);
    }
}
