void drawSquare(in vec2 uv, in vec3 color, in vec2 position,in vec2 size, 
out vec4 outColor)
{
    vec2 posn=-position/iResolution.xy;
    if(uv.x+posn.x>0.0 && uv.x+posn.x<size.x 
    && uv.y+posn.y>0.0 && uv.y+posn.y<size.y)
    {
        outColor=vec4(color,1);
    }
}

void drawCircle(in vec2 uv, in vec3 color, in vec2 position,in float radius, 
out vec4 outColor)
{
    vec2 posn=position/iResolution.xy;
    float distanceToCenter=distance(uv,posn);
    if(radius>distanceToCenter)
    {
        outColor=vec4(color,1);
    }
}

void carrinho(in vec2 pos,in vec2 uv, out vec4 fragColor)
{
    drawSquare(uv,vec3(.5,.6,.5),pos+vec2(1,0),vec2(0.2, 0.1),fragColor);
    drawSquare(uv,vec3(.5,.6,.5),pos+vec2(15,0),vec2(0.1, 0.2),fragColor);
    drawCircle(uv,vec3(.5,.6,.5),pos+vec2(25,-7),0.03,fragColor);
    drawCircle(uv,vec3(.5,.6,.5),pos+vec2(110,-7),0.03,fragColor);
}





