vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
        //-vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.y += (iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    vec2 uv1 = uv;
    uv.y = mod(uv.y,.5)+.25;
    uv.x = mod(uv.x,.5)+.25;

    for(int k = 0; k < 6; k++){
        uv =
            abs(.5-uv+t2*sign(uv.y-uv.x))/1.5+sign(uv.x)/2.
        ;
        b = uv.y>uv.x;
        if(b) uv = uv.yx;
        t2 =
            triangle_wave(uv+.5).yx
            //triangle_wave(uv+.5).yx*(1.5-mod(floor(uv.y),2.)/2.)
        ;
        uv =
            t2+triangle_wave(uv.yx).yx
        ;
        if(uv.y>uv.x)
        //{t2 = t2.yx; uv=uv.yx;}
        col.x =
            abs((uv.x)-col.x)
            //fract((uv.x)-col.x-iTime/4.)
        ;

        //awesome kaleidoscope effect!
        //if(t2.y>t2.x) {t2 = t2.yx; uv=uv.yx-iTime/8.;}

        col.xyz =
            abs(col.yzx-float(b)/2.)
            //abs(vec3(col.zx,max(uv.y-uv.x,col.y)))
        ;
        //if(t2.x>t2.y) col=col.yzx;
    }
    if (!b) col = 1.-col;
    fragColor = vec4(min(col,vec3(1.)),1.0);
}

//remix of: https://www.shadertoy.com/view/csK3zm

#define fmod(x,y) floor(max(mod(floor(-(x))*4.,y),mod(floor(x)*5.,y)))
#define fmod1(x,y) floor(min(mod(floor(-(x))*4.,y),mod(floor(x)*5.,y)))
vec2 mainSound(int samp, float time){  
  float tempo = .5;
  time /= tempo;
  float s2 = pow(2.,1.+fmod1(-time/4.,2.)),
  s1 = pow(2.,fmod(-time*s2*2.,3.)),
  m1 = s1*(1.+fmod(time/s2/s1*2.+1.,2.)),
  t = time/s1/pow(2.,fmod1(time/5./8.,5.))*2.,
  t1 = 1.+fmod(t/s1,2.),
  m4 = fmod1(-t*s1/t1,s2),
  m3 = fmod(-t*s1/m1,s2);
  t *= s1/t1;
  float m2 = 1. + fmod(-t,s1),
  a = 8.*fract(-t),
  nb = time*tempo*pow(2.,(m3+m2)/5.+7.5);
  return abs(abs(1./s1-1./s2)/8.-abs(vec2(sin(nb),cos(nb))))*a;
}
