float PI = 3.14159265358979323846;
float TWOPI = 6.28318530718;
float PIONTWO = 1.57079632679;

float norm(vec2 uv)
{
    return(float(uv.x*uv.x + uv.y*uv.y));
}

float atan2(float y, float x)
{
    //this computes arctan(y/x)
    float angle = atan(y,x);
    if(angle < 0.0){angle+= TWOPI;}
    return(angle);
}

vec2 exp2d(vec2 uv)
{
    return( exp(uv.x)*vec2(cos(uv.y),sin(uv.y)));
}

vec2 log2d(vec2 uv)
{
    return(vec2(log(length(uv)),atan2(uv.y,uv.x)));
}

vec2 power(vec2 uv, float n)
{
    float modulus = length(uv);
    float angle = atan2(uv.y,uv.x);
    angle *= n;

    return( exp(n*log(modulus))*vec2(cos(angle),sin(angle)));
}

vec2 times (vec2 z1, vec2 z2)
{
    return vec2(z1.x*z2.x - z1.y*z2.y , z1.x*z2.y + z1.y*z2.x);
}

vec2 oneoverz(vec2 uv)
{
    float squaremodulus = norm(uv);
    return( vec2(uv.x/squaremodulus, -uv.y/squaremodulus) );
}

vec2 power2d(vec2 uv, vec2 z)
{

    return( exp2d(times(vec2(log(length(uv)),atan2(uv.y,uv.x)),z) ) );
}

vec2 cos2d(vec2 uv)
{
    return(vec2(0.5*( exp2d(times(uv,vec2(0.0,1.0))) + exp2d(times(-uv,vec2(0.0,1.0))))));
}


vec2 sin2d(vec2 uv)
{
    return(times(0.5*( exp2d(times(uv,vec2(0.0,1.0))) - exp2d(times(-uv,vec2(0.0,1.0)))),vec2(0.0,1.0)));
}

vec2 a(vec2 uv, vec2 p, vec2 q, vec2 k)
{
    return( 0.5*(times(uv,oneoverz(p)) - (times(q,uv) + k)) );
}

vec2 b(vec2 uv, vec2 p, vec2 q, vec2 k)
{
    return( 0.5*(times(uv,oneoverz(p)) + times(q,uv) + k) );
}

vec2 syracusepq(vec2 uv, vec2 p, vec2 q, vec2 k)
{
    return( (times(cos2d(PI*uv), a(uv,p,q,k)) + b(uv,p,q,k)) );
}

vec2 syracuse(vec2 uv)
{
    return(-0.25*(times(cos2d(PI*uv),5.0*uv + vec2(2.0,0.0)) - (7.0*uv + vec2(2.0,0.0))));
}

vec2 function(vec2 uv,vec2 c)
{    
    vec2 param = (iMouse.xy/iResolution.xy-0.5)*10.0;
    vec2 p = vec2(1.0,0.5); vec2 q = vec2(1.0,param.y); 
    //float p = 2.1; float q = 3.0;
    vec2 k = vec2(param.x,0.0);
    vec2 result = syracusepq(uv,p,q,k);
    return (times(result,c) + (c));
}

vec3 palette2( float t ) {
    //by Inigo Quilez
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.0,0.1,0.2);

    return vec3(a + b*cos(6.28318530718*(c*(t) + d)));
}

vec3 palette5(float iteration)
{
    float value = 0.5;
    vec3 a = vec3(value);
    vec3 b = vec3(1.0-value);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.3,0.1,0.0);

    return (a + b*cos(TWOPI*(c*iteration + d)));
}

vec2 functionPolar(vec2 uv)
{
    float n = 3.0;
    vec2 c = vec2(0.1,-0.8);
    vec2 result;
    result.x = pow(uv.x,n);
    result.y = n*uv.y;
    return result;
}

vec2 cartesianToPolar(vec2 uv)
{
    float r = length(uv);
    float theta = atan(uv.y,uv.x);
    return vec2(r,theta);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //Offset
    uv = uv - 0.5;
    uv.x += 0.0;

    //Scale
    uv *= 30.0;
    uv.x *= iResolution.x / iResolution.y;

     //Choosing the Zoom
    vec2 zoomPoint = vec2(0.0,0.0);
    //zoomPoint += 3.0*(iMouse.xy/iResolution.xy - 0.5);

    //Zoom
    float zoomAmount = pow(10.0, -max(iTime-3.0,1.0));
    uv = uv*zoomAmount;
    uv += zoomPoint;

    // Algorithm to color the pixel
    float tol = pow(10.0,-6.0);


    float DistanceToPrevious = 1.0;
    float currentModulus = length(uv);
    vec2 previousIteration;
    int iterationAmount;
    int testAmount = 1000;

    //vec2 c = 3.0*(iMouse.xy/iResolution.xy - 0.5);
    //float remainder = 1.0 - mod(iTime/10.0,1.0);
    vec2 c = uv;
    float threshold = pow(10.0,8.0);

    for (iterationAmount = 0; iterationAmount < testAmount; iterationAmount++)
    {
        previousIteration = uv;
        uv = function(uv,c);
        currentModulus = length(uv);
        DistanceToPrevious = length(uv - previousIteration);

        if(DistanceToPrevious < tol){break;}
        if(currentModulus > threshold){break;}
    }



    if(currentModulus < threshold)
    {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }

    else
    {
            iterationAmount = int(log(float(((iterationAmount+1)))));
            int periodFast = 15;
            int residueFast = iterationAmount%periodFast;
            float indexFast = float(residueFast)/float(periodFast);
            vec3 colorFast = palette2(indexFast);

            int periodMedium = 70;
            int residueMedium = iterationAmount%periodMedium;
            float indexMedium = float(residueMedium)/float(periodMedium);
            vec3 colorMedium = palette5(indexMedium);

            int periodSlow = 201;
            int residueSlow = iterationAmount%periodSlow;
            float indexSlow = float(residueSlow)/float(periodSlow);
            vec3 colorSlow = palette2(indexSlow);

            float finalIndex = (indexSlow + indexMedium + indexFast)*0.333333333;
            vec3 finalColor = (colorSlow + colorMedium + colorFast)*0.3333333333;
            fragColor = vec4(finalColor,1.0);
    }
}
