#define pi 3.14159
#define dir(a) vec2(cos(a),sin(a))
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

vec3 black    = vec3(0);
vec3 white    = vec3(1);
vec3 pink     = vec3(250, 170, 170)/255.;
vec3 midPink  = vec3(250, 150, 135)/255.;
vec3 darkPink = vec3(255, 120, 120)/255.;
vec3 red      = vec3(255, 0, 0)/255.;
vec3 bg       = vec3(225, 225, 225)/255.;

// Set color = draw color for pixels inside box
#define b(X,Y,W,H) if (X<=f.x&&f.x<X+W&&Y<=f.y&&f.y<Y+H) \
                       col = vec4(draw, 1);

vec4 drawPiggy(ivec2 f, int x, int y) {
    vec4 col;  // Output color
    vec3 draw; // Draw color

    f -= ivec2(x, y);
    ivec2 f0 = f;
    ivec2 fabs = ivec2(abs(f.x), f.y);

    f = f0;
    draw = pink;
    b(-4,-3,10,6);
    b(-5,3,7,1);
    b(-6,4,4,1);
    b(-5,5,1,1);
    b(3,0,6,3);
    b(7,1,4,3);
    b(-5,-3,1,3);
    b(-4,-4,9,1);
    b(-3,-5,7,1);
    b(-2,-6,5,1);
    b(-1,-7,3,1);
    b(2,-19,1,4);
    b(-3,-19,1,4);

    f = f0;
    draw = white;
    b(-1,-1,3,3);

    draw = black;
    b(0,0,1,1);
    b(2,-20,3,1);
    b(-3,-20,3,1);

    draw = red;
    b(2,-2,1,1);
    b(3,-3,3,1);

    draw = midPink;
    b(-2,-4,2,2);

    f = fabs;
    draw = darkPink;
    b(0,-15,4,1);
    b(0,-14,6,3);
    b(0,-11,5,3);
    b(0,-8,4,1);
    b(2,-7,2,1);

    draw = pink;
    b(4,-8,3,1);
    b(6,-12,1,4);
    b(7,-13,1,1);
    b(5,-13,1,1);

    return col;
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         u = (U+U-R)/R.y;

    float t = iTime;

    // Background
    O.rgb = bg;

    float sc = mix(200., 30., tanh(.4*t));   

    ivec2 f = ivec2(floor(.5*sc*vec2(u.x/sin(u.y+iTime), u.y)));
    vec4 pig = drawPiggy(f, 0, 6);
    pig.a *= .6 + .4 * sin(u.y+iTime);
    O = mix(O, pig, .6*pig.a);

    float n = 8.;
    for (float i; i++ < n;) {
        float o = 2. * pi * i / n;
        float sc2 = sc * (1.2 + .2 * cos(u.y*10.-4.*iTime+o));
        float s = sin(iTime - .5*u.y*cos(iTime-2.*o) + o);
        float c = cos(iTime - .5*u.y*cos(iTime-2.*o) + o);
        ivec2 f = ivec2(floor(sc2*vec2((u.x-s)/c, u.y - .2 * c * cos(iTime))));
        vec4 pig = drawPiggy(f, 0, 4);
        pig.a *= .5 - .5 * c;
        O = mix(O, pig, pig.a);
    }    
}
