mat2 rotation(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

const mat2 m = mat2(-1.1, -.4, .3, 1.0);

float noise( in vec2 p) {
    return sin(p.x) * sin(p.y);
}

float fbm4(vec2 p) {
    float f = 0.0;
    for (float i = 0.; i < 5.; i++) {
        f += 1.5 * .1 * noise(p * i);
        p = m * p * abs(i - 2.) * 2.3;
    }

    return f / 0.99999990;
}

vec2 fbm4_2(vec2 p) {
    return vec2(fbm4(p), fbm4(p + vec2(7.8)));
}


float myNoise( in vec2 f ){
  f *= m;
  return ( cos( f.x + .18975 ) * sin( f.y + .494516 ) + .1615246 );
}

mat2 rot( float d ){
float s = sin(d), c = cos(d);
return mat2(c, s, -s, c);
}

float fbm( in vec2 x, in float H )
{    
    float t = 0.0;
    for( int i=0; i<7; i++ )
    {
        float f = pow( 2.0, float(i) );
        x *= rot( float( i ) );
        float a = pow( f, -H );
        a = smoothstep( 0., .05, a );

        t += a*myNoise(f*x * rot( float(i) ));

    }
    return t;
}

float coreCalc(vec3 pos, int i) {

    vec3 p = pos;

    p.xz *= rotation(3.1415926 * iTime * .1 * float(i) + float(i));

    float d = length(p) - .5 + (fbm4_2(p.yz - iTime * .2).y * 1.5 * fbm4_2(p.xz- iTime * .2).x * 1. * cos(p).y * 1.) * .6;

    return d;
}

float map(vec3 pos) {

    vec3 p = pos;

    float d = coreCalc(p, 1);

    float bg = length(pos) - .1;

    d = mix(d, .05, bg);

    return d;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos) {
    vec2 e = vec2(1.0, -1.0) * 0.5773;
    const float eps = 0.0005;
    return normalize(e.xyy * map(pos + e.xyy * eps) +
        e.yyx * map(pos + e.yyx * eps) +
        e.yxy * map(pos + e.yxy * eps) +
        e.xxx * map(pos + e.xxx * eps));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;



    // Time varying pixel color
    float tt = iTime * .4;

    uv = (uv - .5) * 2.;

    uv.x *= iResolution.x / iResolution.y;


    vec3 origin = vec3(cos(tt) * -1.2, 0.0, sin(tt) * -1.2);
    vec3 ta = vec3(0.0, 0., 0.0);

    origin = vec3(0., 0., -1.);

    // camera matrix
    vec3 ww = normalize(ta - origin);
    vec3 uu = normalize(cross(ww, vec3(0.0, 1.0, 0.0)));
    vec3 vv = normalize(cross(uu, ww));

    vec3 tot = vec3(0.0);

    vec3 ray = normalize(uv.x * uu + uv.y * vv + 1.5 * ww);

    float t = 0.0;

    const float tmax = 5.0;

    for (int i = 0; i < 32; ++i) {
        vec3 p = origin + ray * t;
        float distance = map(p);
        t += distance * 0.9999;
    }

    vec3 col = vec3(0.0);
    vec3 lig = normalize(vec3(.57703));
    vec3 hal = normalize(lig - ray);
    // vec3 spcol = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));




    if (t < tmax) {
        vec3 pos = origin + t * ray;
        vec3 nor = calcNormal(pos);


        float dif = clamp(dot(nor, vec3(.4, 1., -.5)), 0.0, 1.0);
        float p = fbm( vec2( fbm( nor.xy * .5 - iTime * .01, 1. ) ), .9 );

        float spe = pow(clamp(dot(nor, hal), 0.0, 1.0), 50.0) * .6;
        spe *= dif;

        float amb = dot(nor, vec3(0.0, 1.0, 0.0));

        col = spe + vec3( p * .08 + .1 ) * amb + vec3(.6) * dif + vec3(.3);
    }


    // gamma        
    col = sqrt(col);
    tot += col;

    // Output to screen
    fragColor = vec4(tot, 1.0);
}
