// Squares a given complex number
vec2 cSquare( in vec2 z ) 
{
    return vec2(
            z.x * z.y - z.y * z.y,
            2.0 * z.x * z.x
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 c = fragCoord/iResolution.x;

    // Scale [lower is more zoomed in]
    float scale = 5.5;

    c *= scale;

    // Move set into view
    c += vec2(-3.0,-1.5);

    // Maximum number of iterations
    int maxIterations = 256;

	// Starting point
	vec2 Z = vec2(0,0);

    for(int i = 0; i < maxIterations; i++)
    {
        // Z(n+1) = Z(n)^2 + C
        vec2 oldZ = Z;
        Z = (cSquare(oldZ) + (c/cos(iTime/5.0)/10.0) + (oldZ * sin(iTime/2.0)/0.5)) + (sin(c)-cos(c) + tan(c));

        if(dot(Z, Z) > 100.0) {
            // get smooth iters
            float brightness = (float(i) - log2(log2(dot(Z,Z))) + 4.0) / float(maxIterations);

            // multipliers for ( R   G   B ) values
            vec3 weights = vec3(10.0 + sin(iTime/5.0) * 5.0,10.0 + cos(iTime/5.0) * 5.0,2.0);

            fragColor = vec4(vec3(brightness * weights *2.0), 1.0);
            break;
        } else {
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        }
    }
}
