void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_dscl = 1.;// dscl down scale
    vec2 o_lires = iResolution.xy * n_dscl;// lres low resolution
    vec2 o_lfc = fragCoord.xy * n_dscl;

    float n_ratio_1to_iresx = 1./ o_lires.x;

    vec2 o_lfc_nor = floor(o_lfc) / o_lires; // low frag coord normalized
    vec2 o_lfc_nor_off = (floor(o_lfc)-0.5*o_lires.xy)/ o_lires.y;
    vec2 o_fc_nor = fragCoord.xy / iResolution.xy;
    vec2 o_fc_nor_off = (fragCoord.xy-0.5*iResolution.xy) / iResolution.y;
    vec2 o_mo_nor_off = (iMouse.xy-0.5*iResolution.xy) / iResolution.y;

    float n_its = 4.;
    float n_tau = 6.283185307179586;
    float n_ang_nor = fract(atan(o_lfc_nor_off.y, o_lfc_nor_off.x)/n_tau);

    float n_dcntr = length(o_lfc_nor_off);

    float n_one_it_nor = 1./n_its;
    float n_it = floor(n_ang_nor/n_one_it_nor);
    float n_it_nor = n_it / n_its;
    float n_nei = floor((1.-n_dcntr)*n_its);
    vec2 o_len_min = vec2(1.);
    float n_it_min = 0.;

    float n_ang_off = 0.;//n_one_it_nor*0.5;
    vec2 o = vec2(
        cos(((n_it/ n_its)+n_ang_off) * n_tau) * n_dcntr,
        sin(((n_it/ n_its)+n_ang_off) * n_tau) * n_dcntr
    );
    float n_len = length(o_fc_nor_off - o);
    float n_len_nor = n_len/n_dcntr*n_its/4.;// i dont know if this is correct, probably not...
    //n_len_nor = smoothstep(0.1, 0.11, n_len_nor);

    if(iMouse.z == 0.){
        float n_t = iTime*2.;
        o_mo_nor_off = vec2(sin(n_t), cos(n_t)*0.5+0.5);
    }

    fragColor = vec4(n_len);

    if(o_fc_nor_off.x > o_mo_nor_off.x){
        fragColor = vec4(smoothstep(0.1, 0.1, n_len));

    }




}
