#define BLACK vec3(0.0)
#define RED vec3(1.0, 0.01, 0.01)

// Polynomial smooth max from IQ.
float smax(float a, float b, float k)
{
	float h = clamp(0.5 + 0.5 * (b-a)/k, 0.0, 1.0);	
    return mix(a, b, h) + k*h*(1.0-h);
}

float Heart(vec2 uv, float b)
{
	// Widen the heart by stretching the underlying coordinates.
    uv.x *= .8;

    // Smooth max removes the sharp edge in the middle of the heart. 
    uv.y -= smax(sqrt(abs(uv.x)), b, 0.3*b)*.3;

    return smoothstep(b, -b, length(uv)-0.1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
   	vec2 uv = (fragCoord.xy-.5*iResolution.xy) / iResolution.y;

    // Make the circle of hearts wavy.
    float t = uv.x*7.0 - 0.8*iTime + uv.x*1.1;
    uv.y -= sin(t)*0.07;

    // Polar coordinates
    vec2 st = vec2(atan(uv.x, uv.y), length(uv));    

    // st.x: transform -PI to PI into 0 to 1.
    // Make it twisting and rotating by adding iTime and st.y.
    uv = vec2(st.x / 6.28 + .5 + 2.5 * st.y * sin(iTime*0.2), st.y);
    uv *= vec2(3.0, 1.2);

    float d = 0.0;

    // X goes outside of the visible x-axis since adding iTime and twisting stuff 
    // to the polar coordinates makes the non-visible hearts appear.
    for (float x = -2.8; x < 5.8; x += 0.30) {
        d += Heart(uv-vec2(x, 0.28), 0.01); 
    }

    vec3 col = mix(BLACK, RED, d);

    // Add shading.
    col *= .75 + cos(t) * 0.2;

    fragColor = vec4(col, 1.0);
}
