float fbm(vec2 u,float t)
{
    float l = length(u)-t*.15;
    l = sin(l)+sin(l*2.3)+sin(l*3.1);
    float a = 1.-l*.6;
    mat2 m = mat2(.8,.6, -.6,.8)*2.;
    u*=.007;
    a += .50*texture(iChannel0,u).x; u *= m;
    a += .25*texture(iChannel0,u).x; u *= m;
    a += .12*texture(iChannel0,u).x; u *= m;
    a += .06*texture(iChannel0,u).x; u *= m;
    a += .03*texture(iChannel0,u).x;
    return a;
}
float tal(vec2 u,vec2 n)
{
    float s = 1.;
    for(float i=0.; i<8.; ++i)
    {
        u = abs(u);
        float l = 1.5/dot(u,u);
        s*=l;
        u*=l;
        u+=n;
    }
    return -abs(u.y)/s;
}
float tal2(vec2 u,vec2 n,out vec4 o)
{
    vec2 m = vec2(6);
    float s = 1.;
    for(float i=0.; i<8.; ++i)
    {
        u = abs(u);
        float l = 1.5/dot(u,u);
        s*=l;
        u*=l;
        u+=n;
        o+=(cos(i*m.x+l+vec4(2,1,3,4)*m.y))*(.1-abs(u.y)/s);
    }o=o*.5+1.;
    return -abs(u.y)/s;
}
vec4 my(vec2 u, vec2 pp)
{
    vec2 v = u*10.;
    float t = pp.y;
    float a = fbm(v-vec2(2,88)*.0001,t)-fbm(v,t);
    vec4 b = a*7.+vec4(.77,.74,.67,0);
    vec2 n = cos(-t*.1+vec2(.0,.5)*3.145)*.02;
    n -= vec2(2.41,.09);
    v = vec2(abs(u.x)+.2,u.y);
    vec4 c = vec4(0);
    float s = tal2(v,n,c);
    c*=(tal(v-vec2(2,88)*.00005,n)-s)*22.+.7;
    c*=s*3.+1.1;
    s = s*4.+.5-a*8.;
    b = mix(b,c,clamp(s+.5,0.,1.));
    b = mix(b,c,clamp(s*6.,0.,1.));
    return b;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 1.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = 1.*(2.*iMouse.xy-iResolution.xy)/iResolution.y;

    fragColor = my(u, vec2(iTime*4.));
}
