const vec2 vp = vec2(320.0, 200.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trmono_nooffset = iMouse.xy / iResolution.xy;
    if(iMouse.z == 0.) o_trmono_nooffset = vec2(0.3, 0.);
    vec2 o_scale_factor = vec2(o_trmono_nooffset.x*o_trmono_nooffset.x);
    vec2 o_fc = floor(fragCoord.xy * o_scale_factor);
    vec2 o_ires = floor(iResolution.xy * o_scale_factor);
    //from now on: make sure to replace
    //             fragCoord   and  iResoltuion 
    // with
    //             o_fc        and  o_ires

	float t = iTime * 10.0 + iMouse.x;
	vec2 uv = o_fc.xy / o_ires.xy; // <-- this was "vec2 uv = fragCoord.xy / iResolution.xy;"
    vec2 p0 = (uv - 0.5) * vp;
    vec2 hvp = vp * 0.5;
	vec2 p1d = vec2(cos( t / 98.0),  sin( t / 178.0)) * hvp - p0;
	vec2 p2d = vec2(sin(-t / 124.0), cos(-t / 104.0)) * hvp - p0;
	vec2 p3d = vec2(cos(-t / 165.0), cos( t / 45.0))  * hvp - p0;
    float sum = 0.5 + 0.5 * (
		cos(length(p1d) / 30.0) +
		cos(length(p2d) / 20.0) +
		sin(length(p3d) / 25.0) * sin(p3d.x / 20.0) * sin(p3d.y / 15.0));
    fragColor = texture(iChannel0, vec2(fract(sum), 0));
}

const float pi = 3.1415926435;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trmono_nooffset = iMouse.xy / iResolution.xy;
    if(iMouse.z == 0.) o_trmono_nooffset = vec2(0.3, 0.);
    vec2 o_scale_factor = vec2(o_trmono_nooffset.x*o_trmono_nooffset.x);
    vec2 o_fc = floor(fragCoord.xy * o_scale_factor);
    vec2 o_ires = floor(iResolution.xy * o_scale_factor);

    float i = o_fc.x / o_ires.x;
    vec3 t = (iTime + iMouse.y) / vec3(63.0, 78.0, 45.0);
    vec3 cs = cos(i * pi * 2.0 + vec3(0.0, 1.0, -0.5) * pi + t);
    fragColor = vec4(0.5 + 0.5 * cs, 1.0);
}
