#define MODE 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 M = iMouse.xyz / iResolution.xyz;

    uv *= 16.0;
    M.xy *= 16.0;
    vec2 col = floor(uv);
    vec2 MCol = floor(M.xy);
    uv = fract(uv);
    M.xy = fract(M.xy);
    uv.x = 1.0 - uv.x;
    M.x = 1.0 - M.x;

    float r = uv.x, g = uv.y, b = (col.x + (col.y * 16.0)) / 255.0;

    if (iMouse.z >= 1.0) {
        if (MODE == 1) {
            vec2 baseUv = fragCoord / iResolution.xy;
            r = 1.0 - baseUv.x;
            g = baseUv.y;
        } else {
            r = M.x;
            g = M.y;
        }
        b = (MCol.x + (MCol.y * 16.0)) / 255.0;
    }

    fragColor = vec4(1.0 - vec3(r, g, b), 1.0);
}
