// "truchet3d toroidal hexagon tiles"
//
// created by Colling Patrik (cyperus) in 2023
//
// References, Inspiration:
//
// - "Schwarz-Christoffel Mapping" by mla
//   https://www.shadertoy.com/view/tsfyRj
//
// - "Year of Truchets" #058 by byt3_m3chanic
//   https://www.shadertoy.com/view/msVfzR
//
////////////////////////////////////////////////////////////////////////////////

// ray casting bounding shape
const float CAM_DIST = 4.; 
const float CAM_FLE = 2.;
const float BB_DIST = 4.;
const float RAY_MIN_DIST = 0.1;
const float RAY_MAX_DIST = CAM_DIST+1.1*BB_DIST;

int   MAX_MARCHING_STEPS = 900;
const float RAY_STEP_FACTOR3 = 180./450.;
float RAY_STEP_FACTOR  = (0.5-0.0001)* pow(RAY_STEP_FACTOR3, 3.) + 0.0001;
float RAY_PRECISION = 3.E0;

// shape mod
float RPT_a0 = 6.0;

// fog
const float FOG_DENSITY = 1.E-2;

// data from sdf
vec4 mat = vec4(0);

float map(in vec3 p) {
    float rxy = 0., au = 0.0, av = 0.0;
    vec2 z = vec2(0.);
    float d = -1.;

    /// regular polygon tiles
    vec3 rpt_id = vec3(1.);
    p.xy = inversesc(hexgrid2hex(p.xy, rpt_id),6);

    /// torus,sphere,2spheres       
    // cylinder radial plane -> halfcomplex plane
    rxy = length(p.xy); au = atan(p.y, p.x);
    z = 2.*ccos(PI*vec2(rxy,p.z)); z.x -= RPT_a0;   

    p = vec3(log(length(z)), 0.5*au/PI, 0.5*atan(z.y,z.x)/PI);
    z = p.yz;

    /// truchet 3D
    vec3 tc_id = vec3(0.); float tc_a = 0.;
    // truchet grid
    float TC3D_nu = 3.*2.*3.;
    float TC3D_nv = 2.*2.;
    float TC3D_nr = 1.;

    p *= vec3(TC3D_nr,TC3D_nu,TC3D_nv); // r,u,v
    p.yzx = p.xyz;

    ivec3 l0 = -ivec3(TC3D_nv/2.,0,TC3D_nu/2.);
    ivec3 l1 = ivec3(TC3D_nv/2.,TC3D_nr,TC3D_nu/2.)-ivec3(1.);
    tc_id = floor(p);
    tc_id = clamp(tc_id, vec3(l0), vec3(l1));
    vec3 q = p - tc_id -0.5;

    // random
    float rnd = hash21(tc_id.xz);

    // flip cell direction randomly
    if(rnd<.5) q.x = -q.x;

    // sdf
    float r0 = 0.5; float r1 = 0.125;

    if(fract(rnd*77.57+iTime/6.)>.75) {
       // offset-xz circle, id diagonal sub domain
       vec3 q2 =  length(q.xz-.5)<length(q.xz+.5)
                ? vec3(q.xz-.5,0.)  
                : vec3(q.xz+.5,1.);
       d = sdTorus( vec3(q2.x, q.y, q2.y) , vec2(0.5,r1) );
    }
    else {
       d = opSmoothUnion( sdCylinder( q.zxy, r1 ), sdCylinder( q.xzy, r1 ), 0.05 );
    }

    // cell alpha    
    tc_a = sdBox( q, vec3(r0*0.9)); float px = fwidth(tc_a);
    tc_a = smoothstep(px,-px,tc_a);    

    /// return
    // torus uv, truchet cell alpha, random cell id
    mat.xy = z; mat.z = tc_a, mat.w = rnd;
    return d;
}

// intersect
float intersect(in vec3 ro, in vec3 rd, in float px) {
    float res = -1.0; // init no intersection

    // bb
    vec2 dis = isphere( ro, rd , abs(BB_DIST));    
    if( dis.y<0.0 ) return -1.0;
    dis.x = max( dis.x, RAY_MIN_DIST );
    dis.y = min( dis.y, RAY_MAX_DIST );

    // raymarch signed distance field    
	float fh = RAY_STEP_FACTOR;
    float t = dis.x;
    for( int i=0; i<MAX_MARCHING_STEPS; i++  ) {
        vec3 pos = ro + rd*t;
        float th = RAY_PRECISION * px * t;
        float h = map( pos);
        if( t>dis.y || h<th ) break;
        t += h*fh;
    }
    if( t<dis.y ) {
        res = t;
    }
    return res;
}

vec3 calcNormal( in vec3 p, in float prec ) {
    vec2 e = vec2( prec, 0. );
    return normalize(vec3(
		map(p+e.xyy) - map(p-e.xyy),
		map(p+e.yxy) - map(p-e.yxy),
		map(p+e.yyx) - map(p-e.yyx) )
    	);
}

vec3 transform(in vec3 p, in float time) { 
	if (iMouse.x > 0.0) {
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
		p.yz = crot(p.yz,-PI*m.y);
		p.zx = crot(p.zx,-PI*m.x);
	}
    p.yz = crot(p.yz,-2.*PI*1.*time/360.);
    p.zx = crot(p.zx,-2.*PI*3.*time/360.);
	return p.xyz;
}

// textures
vec4 ribbon(in float uv_x, in float time) {
    float sdf = -(2.*pulsecollision(1.,1./3. * uv_x, time, 2./6.)-1.);
    float px = fwidth(sdf);
    return vec4( h2rgb(sdf -2./6.)
               , smoothstep(px+0.1,-px-0.1,sdf+0.2) );
}

vec4 truchet3D(in float rnd, in float a ,in float time) {
    return mix(vec4(1.), vec4(h2rgb( fract(abs(rnd)*22.93 + time)),1.), a);
}

//
void mainImage(out vec4 O, in vec2 fragCoord) {
    // modulation shape
    RPT_a0 = -(3.+ 2. *sin(2.*PI*iTime/60.));

    // anti-aliasing to take a picture
    vec3 tot = vec3(0.0);
    #define AA 1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {       
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 uv = (2.0*(fragCoord+o) - iResolution.xy)/iResolution.y;

        // camera viewport --> worldspace
        vec3 ro = vec3(0,0,-CAM_DIST);
        vec3 rd = vec3(-uv.x,uv.y,CAM_FLE);
             ro = transform(ro,iTime);
             rd = normalize(transform(rd,iTime));

        // distance hit ray surface
        float px = 2.0/( iResolution.y*CAM_FLE );
        float t = intersect( ro, rd, px );

        vec3 sky_col = rd*.4+.6;

        // background
        vec4 C = vec4(sky_col,1.);
        O = C;

        // object surface
        if (t >= 0.) {
            // surface properties
            vec3 p = ro + rd * t;
            vec3 n = calcNormal( p, 0.001);

            // uv-coords
            vec2 mat_uv = mat.xy;

            // draw texture layers from back to front
            O = vec4(0);
            #if 1
            if(O.a<1.) {             
                C = ribbon( 3.*6.* mat_uv.x, 0.0125*iTime); C.rgb *= 0.7;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            #endif
            if(O.a<1.) {
                C = vec4(vec3(n)*0.5,0.2); // normal
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            if(O.a<1.) {            
                C = truchet3D(mat.w, mat.z, iTime/60.); C.rgb *= 0.5;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            C = O;

            // lighting
            vec3  came_li_o   = transform(vec3(-10.,10.,-10.),iTime); // origine
            vec3  came_li_dir = normalize(p - came_li_o);             // direction
            vec3  came_li_col = vec3(1.,1.,.5);                       // color
            float came_li_shi = 10.;                                  // shininess            

            float cosa = 1.;
            O.rgb += 0.1 * cosa * C.rgb * sky_col;           // amb
            cosa = clamp(dot(-came_li_dir, n), 0., 1.);
            O.rgb += 0.5 * cosa * C.rgb * came_li_col;       // dif       
            cosa = pow(clamp(dot(reflect(came_li_dir, n), -rd), 0., 1.), came_li_shi);           
            O.rgb += 0.4 *(cosa>0.? cosa:0.) * came_li_col ; // spec

            // fresnel
            O.rgb += pow(clamp(1. - dot(n, -rd), 0., 1.), 5.) * 0.6 * sky_col;

            // fog
            O.rgb = mix(O.rgb, sky_col, 1.0 - exp(-FOG_DENSITY * pow(t,3.)));    
        }
        tot += O.rgb;
    }
    tot /= float(AA*AA);

    // gamma correction
    O = vec4(rgb2srgb(tot),1.);
}

// const
const float PI = abs(atan(0.,-1.));

/// 1D transformations
float pulsecollision(in float pf, in float x, in float x0, in float pw) {
    // x  : periodic in[0,1]
    // x0 : offset
    // pw : pulse width in[0,1]
    // pf : pulse frequency
    float ntriw_p = abs( fract( pf*(x - x0) )-0.5); // x_normalized triangle wave function moving in +x_direction
    float ntriw_n = abs( fract( pf*(x + x0) )-0.5); // x_normalized triangle wave function moving in -x_direction
    float pp = smoothstep( 0.5*pw, 0., ntriw_p );   // pulse moves in +x_direction
    float pn = smoothstep( 0.5*pw, 0., ntriw_n );   // pulse moves in -x_direction
    return (pp+pn); // in[0.,2.]
    //return (pp+pn)-(pp*pn);// in[0.,1.]
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453);}

/// 2D transformations
/// complex
vec2 cmul(vec2 za,vec2 zb) {
    return za*mat2(zb.x,-zb.y,zb.yx);}

vec2 crot(vec2 z,float a) {
    return cmul(vec2(cos(a),sin(a)),z);}

vec2 cinv(vec2 z) {
    return z*vec2(1,-1)/dot(z,z);}

vec2 cdiv(vec2 z, vec2 w) {
    return cmul(z,cinv(w));}

vec2 cpow(vec2 z, int n) {
  float r = length(z);
  float theta = atan(z.y,z.x);
  return pow(r,float(n))*normalize(vec2(cos(float(n)*theta),sin(float(n)*theta)));}

vec2 csqr(vec2 z) {
    return vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y );}

vec2 ccos(vec2 z) {
    vec2 d=vec2(exp(z.y),exp(-z.y));
	return vec2(cos(z.x)*(d.x+d.y)*.5,-sin(z.x)*(d.x-d.y)*.5);}

float binomial(float a, int n) {
   float s = 1.0;
   for (int i = n; i >= 1; i--,a--) {
     s *= float(a)/float(i);
   }
   return s;
}

vec2 expi(float x) {
  return vec2(cos(x),sin(x));
}

float gamma(float z) {
  const float[8] p = float[](
    676.5203681218851,
    -1259.1392167224028,
    771.32342877765313,
    -176.61502916214059,
    12.507343278686905,
    -0.13857109526572012,
    9.9843695780195716e-6,
    1.5056327351493116e-7
  );
  z -= 1.0;
  float x = 0.99999999999980993;
  for (int i = 0; i < 8; i++) {
    float pval = p[i];
    x += pval/(z+float(i+1));
  }
  float t = z + 8.0 - 0.5;
  return sqrt(2.0*PI) * pow(t,z+0.5) * exp(-t) * x;
}

// beta function
float B(float a, float b) {
  return (gamma(a)*gamma(b))/gamma(a+b);
}

vec2 inversesc(vec2 z, int K) {
  float cn[6];
  for (int n = 1; n <= 5; n++) {
    cn[n] = binomial(float(n)-1.0+2.0/float(K),n)/float(1+n*K);
  }
  float C = B(1.0/float(K),1.0-2.0/float(K))/float(K);
  z *= C;
  vec2 h = cpow(z,int(K));
  float T1 = -cn[1];
  float T2 = -cn[2]+float(K+1)*pow(cn[1],2.0);
  float T3 = -cn[3]+float(3*K+2)*(cn[1]*cn[2]-float(K+1)/2.0*pow(cn[1],3.0));
  float T4 = -cn[4]+float(2*K+1)*(2.0*cn[1]*cn[3]+pow(cn[2],2.0)-float(4*K+3)*
                                  (pow(cn[1],2.0)*cn[2]-float(K+1)/3.0*pow(cn[1],4.0)));
  float T5 = -cn[5]+float(5*K+2)*(cn[1]*cn[4]+cn[2]*cn[3]+float(5*K+3)*
            (-0.5*pow(cn[1],2.0)*cn[3]-0.5*cn[1]*pow(cn[2],2.0)+float(5*K+4)*
            (pow(cn[1],3.0)*cn[2]/6.0-float(K+1)*pow(cn[1],5.0)/24.0)));
  vec2 X = vec2(1,0)+h/pow(C,float(K));
  vec2 w = cmul(z,vec2(1,0) + T1*h + T2*cpow(h,2) + T3*cpow(h,3) + T4*cpow(h,4) + cdiv(T5*cpow(h,5),X));
  return w;
}

vec2 hexgrid2hex(in vec2 Z, out vec3 U_id){
    const float SQRT3 = sqrt(3.);
    const float SQRT_3 = 1./SQRT3;
	const mat2 M = mat2(SQRT_3,-1.,-SQRT_3,-1.),
        iM = 0.5*mat2(SQRT3,-SQRT3,-1.,-1.);
    Z = crot(Z,PI/6.);		// F:orientation-offset of cell     
    Z = iM*Z/0.75;          // F:scale cell
    vec3 U = vec3(Z.x,-Z.x-Z.y,Z.y);// plane: x+y+z=0
    U_id = 2.*floor(U*0.5+0.5);		// global cell offset id 
    vec3 U_lo = U-U_id;				// local cell coord's
    vec3 aU_lo = abs(U_lo);
    if (aU_lo.x > aU_lo.y && aU_lo.x > aU_lo.z) U_lo.x = -U_lo.y-U_lo.z;
    if (aU_lo.y > aU_lo.z) U_lo.y = -U_lo.x-U_lo.z;
    else U_lo.z = -U_lo.x-U_lo.y;
    Z = U_lo.rb; 			// mape grid-zell to unit-zell
    Z = M*Z*0.75; 			// B:scale of cell
    return crot(Z,-PI/6.);	// B:orientation-offset of cell  
}

// bb
vec2 isphere(in vec3 ro, in vec3 rd, in float r) {
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;   
    if( h<0.0 ) return vec2(-1.0);
    h = sqrt( h );
    return -b + vec2(-h,h);
}

// sdf
float opIntersection( float d1, float d2 ) {
    return max(d1,d2); }

float opSmoothUnion( in float d1, in float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere( vec3 p, float s ) {
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCylinder( vec3 p, float r ) {
  return length(p.xz)-r;
}

float sdTorus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// color
vec3 h2rgb(float h){
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return rgb*rgb*(3.0-2.0*rgb); // cubic smoothing
}

vec3 hsv2rgb(float h, float s, float v){     
    return v * mix( vec3(1.0), h2rgb(h), s); // hue, saturation, value
}

// srgb <--> rgb (linear)
vec3 srgb2rgb(vec3 col) {
    return pow(col, vec3(2.2));
}

vec3 rgb2srgb(vec3 col) {
    return pow(col, vec3(1./2.2));
}
