float SEGMENT_TIME = 4.0;

const float PI = 3.1415926535;

#ifndef TAU
#define TAU 6.2831853071795864769252867665590
#endif

float segmentAngle;

float polySDFScale(vec2 uv, vec2 s, int V) {
    uv = uv*2.-1.;
    float a = atan(uv.x,uv.y)+PI;
    float r = length(uv);
    float v = TAU/float(V);
    float final = cos(floor(.3+a/v)*v-a)*r;
    return final;
}

vec2 rotate(vec2 st, float a) {
    st = mat2(cos(a),-sin(a),
              sin(a),cos(a))*(st-.5);
    return st+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (gl_FragCoord.xy - iResolution.xy * .5) / iResolution.yy + 0.5;

    vec2 rot = rotate(uv, PI + (iTime * 0.075));
    uv = rot;

    float radius = length(uv);

    float poly = polySDFScale(
        uv,
        vec2(2.7),
        6
    ); 
    vec2 st = vec2(cos(poly + iTime), sin(poly - iTime)) * 7.3;

    st = max(min(st, 2.3 / st), st - 2.0);

    vec3 color = vec3(0.0, st);

    float len = length(uv - 0.5);

    color.r = sin(len * 40.0 + poly * 40.0 * - 0.5);
    color.g += cos(len * 30.0 + poly * 60.0 * -sin((iTime / 15.0)));
    color.b += sin(len * 50.0 + poly * 50.0 * -sin((iTime / 15.0)));

    fragColor = vec4(color, 1.0);
}
