float f_n_dist_square(vec2 o, vec2 o2){
    vec2 o_delta = abs(o2-o);
    return max(o_delta.x, o_delta.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;

    vec2 o_scale = vec2(0.02);

    vec2 o_fragCoord_scaled = (fragCoord.xy*o_scale);
    vec2 o_iResolution_scaled = (iResolution.xy * o_scale);
    vec2 o_fragCoord_scaled_floor = floor(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_floor = floor(o_iResolution_scaled);
    vec2 o_fragCoord_scaled_fract = fract(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_fract = fract(o_iResolution_scaled);



    vec2 o_trpino_scldfloor = (o_fragCoord_scaled_floor.xy - o_iResolution_scaled_floor.xy*0.5)/ o_iResolution_scaled_floor.y;



    float n_index = o_fragCoord_scaled_floor.y * o_iResolution_scaled_floor.x + o_fragCoord_scaled_floor.x;
    float n_index_nor = n_index / (o_iResolution_scaled_floor.x * o_iResolution_scaled_floor.y);

    float n_t = sin(iTime)*0.5+0.5;
    n_t = (iTime*.1);
    float n_dist_sqr = f_n_dist_square(o_trpino, vec2(0.0));
    n_dist_sqr = fract(sin(n_t)*0.5+0.5+n_dist_sqr);
    n_dist_sqr = 1.-n_dist_sqr;
    vec2 o_p = o_fragCoord_scaled_fract+vec2(-o_trmono);

    float n_dist_sqr_scaled_fract = f_n_dist_square(o_fragCoord_scaled_fract+vec2(-0.5), vec2(0.0));
    n_dist_sqr_scaled_fract = fract(n_t+n_dist_sqr_scaled_fract+n_index+n_t*10.);

    float n_dists_delta = abs(n_dist_sqr - n_dist_sqr_scaled_fract);
    float n_dists_sum = n_dist_sqr + n_dist_sqr_scaled_fract;
    float n_dists_product = n_dist_sqr * n_dist_sqr_scaled_fract;


    fragColor = vec4(
        mix(n_dist_sqr,n_dist_sqr_scaled_fract, fract(n_dists_sum)),
        mix(n_dist_sqr,n_dist_sqr_scaled_fract, (n_dists_delta)), 
        n_dists_product, 
        1.

    );
    //fragColor = sqrt(fragColor);


}
