// common vals
#define PI 3.1415927
#define EPSILON 0.0001
// implement quality options
#ifdef HI_QUALITY
    #define STEP_MAX 600.
    #define DIST_MAX 35.
    #define STEP_FAC 0.4
#elif defined(LO_QUALITY)
    #define STEP_MAX 100.
    #define DIST_MAX 20.
    #define STEP_FAC 1.
#else
    #define STEP_MAX 160.
    #define DIST_MAX 35.
    #define STEP_FAC 0.7
#endif
// RAY_MAX > 1 is pretty busted currently
#define RAY_MAX 1.
#define SPECULAR_POWER 100.
#define SPECULAR_INTENSITY .5

// ðŸŒˆ
#define PRIDE

// define textures
// note: most of these are unused
struct Tex {
    vec3  c; // color
    float m; // mirror
    float r; // rough
};
// tex setup. takes object and global position as inputs
Tex T1(in vec3 p, in vec3 gp) {
    // green, nonreflective
    Tex T;
    T.c = vec3(0.,1.,0.);
    T.m = 0.;
    return T;
}
Tex T2(in vec3 p, in vec3 gp) {
    // black and white stripes
    // the black part is reflective
    Tex T;
    float tt = iTime * 0.1;
    float stripes = sin(40.*(p.x + sin(p.z*-.5)));
    stripes = SS(0.,0.01,stripes);
    T.c = vec3(stripes);
    T.m = 1.-stripes;
    return T;
}
Tex T3(in vec3 p, in vec3 gp) {
    // show position
    Tex T;
    T.c = normalize(p);
    T.m = 0.;
    return T;
}
Tex T4(in vec3 p, in vec3 gp) {
    // black mirror
    Tex T;
    T.c = vec3(.001);
    T.m = .8;
    return T;
}
Tex T5(in vec3 p, in vec3 gp) {
    // white mattte
    Tex T;
    T.c = vec3(1.);
    T.m = .0;
    return T;
}
Tex T6(in vec3 p, in vec3 gp) {
    // voronoi
    Tex T;
    T.c = voronoi(gp*.5 - iTime).rrr * voronoi(gp*3. - iTime) * voronoi(gp*8. - iTime);
    T.c = mix(T.c, vec3(0.,1.8,1.),gp.y/3.);
    T.c = clamp(vec3(0.),vec3(1.),T.c);
    T.c = hueShift(T.c, T.c.g*8.);
    T.c.g -= 0.5;
    T.c = hueShift(T.c, noise(gp*2. + iTime*0.1)*3.);
    T.m = 0.;
    return T;
}
Tex T7(in vec3 p, in vec3 gp) {
    // horiz stripes
    Tex T;
    T.c = vec3(smoothstep(0.,0.01,sin(p.y*40.)));
    T.m = 0.;
    return T;
}
vec3 rainbowSmooth(float x, float k){
    // return vec3(1.);
    vec3 r1 = vec3(1.,0.,0.)*1.;
    vec3 r2 = vec3(1.,.5,0.)*1.;
    vec3 r3 = vec3(1.,1.,0.)*1.;
    vec3 r4 = vec3(0.,.5,0.)*1.;
    vec3 r5 = vec3(0.,0.,1.)*1.;
    vec3 r6 = vec3(.5,0.,.5)*1.;
    x = fract(x+0.5/6.);

	float level = x * 6.0;
    // float mixamt = fract(level); // full linear mix
    float mixamt = smoothstep(0.5-k,0.5+k,fract(level)); // smoothstep
    vec3 col = vec3(0.);
    if (level<1.) col = mix(r1,r2,mixamt);
    else if (level<2.) col = mix(r2,r3,mixamt);
    else if (level<3.) col = mix(r3,r4,mixamt);
    else if (level<4.) col = mix(r4,r5,mixamt);
    else if (level<5.) col = mix(r5,r6,mixamt);
    else if (level<6.) col = mix(r6,r1,mixamt);
    return col;
}
Tex T8(in vec3 p, in vec3 gp) {
    // rainbow stripes with mirrors
    Tex T;
    float tt = iTime * 0.1;
    float stripes = sin(40.*(p.x + sin(p.z*-.5)));
    vec3 rb = rainbowSmooth(0.2+1.06*(p.x+sin(p.z*-.5)), 0.2);
    stripes = SS(0.,0.1,stripes);
    stripes = stripes*0.8 + 0.2; // glowy rainbow on the mirrors - disable for cleaner color palette (ie for printing)
    rb *= stripes;

    T.c = rb;
    T.m = 1.-stripes;
    return T;
}
Tex T9(in vec3 p, in vec3 gp) {
    // horiz rainbow stripes
    Tex T;
    T.c = rainbowSmooth(-p.y*3.,0.025);
    T.m = 0.;
    return T;
}
// structs for abstracting march process
struct MapData {
    vec4  c; // color
    float d; // distance
    float m; // mirror
    bool  h; // hit
};
struct MarchData {
    vec4  c; // color
    vec3  p; // intersection point
    float d; // distance
    float s; // steps
    bool  h; // hit
    // bounces
    // attenuation
    // steps in this bounce
};
void drawTex(in vec3 p, in vec3 gp, inout MapData m, int tx) {
    Tex T;
    if (tx==1) T = T1(p,gp);
    if (tx==2) T = T2(p,gp);
    if (tx==3) T = T3(p,gp);
    if (tx==4) T = T4(p,gp);
    if (tx==5) T = T5(p,gp);
    if (tx==6) T = T6(p,gp);
    if (tx==7) T = T7(p,gp);
    if (tx==8) T = T8(p,gp);
    if (tx==9) T = T9(p,gp);
    float color_mix = 1.; // this should be set based on material & incidence angle
    m.c = vec4(T.c,color_mix);
    m.m = T.m;
}
// 3d scene
void addObject(in float sdf, in int tx, in vec3 p, in vec3 gp, inout MapData m) {
    m.h = m.h || sdf < EPSILON; // register hit
    if (sdf < m.d) { // this is the closest object so far
        m.d = sdf;
        if (m.h) drawTex(p,gp,m,tx);
    }
}
MapData map(in vec3 p) {
    // returns distance to nearest surface from a given point in 3d space
    MapData m;
    m.d = 9999.; // start high, use min(d,x) to add geometry to scene
    m.m = 0.; // no reflective surfaces yet
    m.h = false; // no surfaces at all yet really
    m.c = vec4(0.); // sure is dark in here...

    // base gyroid
    float gy1 = sdGyroid(p, pow(p.y,.125) - p.y*.2 - 2.);
    int gyroid_mat = 7; // 6
    #ifdef PRIDE
        gyroid_mat = 9;
    #endif
    // if (cell_rand > 0.5) gyroid_mat = 5;
    addObject(gy1, gyroid_mat, sin(p*0.5), p, m);


    // ground/floor plane
    float ground = p.y;

    vec3 gy1_1p = p;
    // gy1_1p.y = floor(gy1_1p.y*10.)/10.;
    float gy1_1 = sdGyroid(gy1_1p, -1.4 * p.y);
    // ground = smin(ground, gy1_1, 0.2);
    ground = fOpUnionStairs(ground, gy1+p.y-.5, .5, 10.);
    // addObject(gy1_1, gy1_1mat, p, p, m);
    int ground_mat = 2;
    #ifdef PRIDE
        ground_mat = 8;
    #endif
    addObject(ground, ground_mat, p, p, m);
    // orbs
    // float orbs = sdOrb(p, 1.4);
    // int orbs_mat = 3;
    // if (cell_rand > 0.5) gyroid_mat = 5;
    // addObject(orbs, orbs_mat, p, p, m);

    vec3 orbonoi = voronoi(p/2.);
    float orbs = orbonoi.x - .3*pow(hash11(orbonoi.z),3.);
    int orbs_mat = 2;
    #ifdef PRIDE
        orbs_mat = 8;
    #endif
    addObject(orbs, orbs_mat, p, p, m);

    return m;
}
vec3 getNorm(vec3 p, float spread) { 
    float d = map(p).d; // Distance
    vec2 e = vec2(.02*spread,0); // Epsilon
    vec3 n = d - vec3(
        map(p-e.xyy).d,  
        map(p-e.yxy).d,
        map(p-e.yyx).d);   
    return normalize(n);
}
vec3 getNorm(vec3 p) {
    return getNorm(p, 0.005); // default spread
}


// raymarch
MarchData raymarch(in vec3 ro, in vec3 rd) {
    MarchData d;
    d.h = false;
    d.d = 0.;
    d.c = vec4(0.);
    float atten = 1.;
    int bounces = 0;
    for (d.s=0.;d.s<STEP_MAX&&d.d<DIST_MAX;d.s++) {
        vec3 p = ro + rd*d.d;
        MapData m = map(p);
        if (m.h) { // hit
            d.h = true; // record hit
            d.p = p; // hit location
            d.c.rgb += m.c.rgb*m.c.a*atten; // l.o.l adds hit color to return color
            if (m.m<0.01) break; // if not a mirror, we're done
            // else, we hit a mirror. reflect and keep steppin
            bounces++;
            if (bounces>2) break;
            atten *= m.m; // - step(.5,p.y);
            vec3 n = getNorm(p);
            rd = reflect(rd, n);
            d.d = 0.;
            // d.c = vec4(n,1.); break; // debug
            ro = p + rd*EPSILON; // step away from surface
        } else
            d.d += m.d*STEP_FAC; // * .5; // halfstep riddim
    }
    return d;
}
float nnoise2(in vec3 x) { // -1 to 1
    float nn = noise(x)*2.-1.;
    return nn*nn; // curved
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // setup
    vec2 R = iResolution.xy;
    vec2 uvc = (fragCoord-R/2.)/R.y;
    vec3 col = vec3(0.);
    float t = iTime;
    // t = 44.06;
    float carloop = mod(t*2.-2.2,101.);
    vec3 carpos = vec3(sin(t*0.3)+2.,.5,t*0.5);
    carpos.y -= smoothstep(32.,40.,carloop) - smoothstep(70.,80.,carloop);
    vec3 sideview = vec3(iMouse.xy/R,-1.);
    // vec3 ro = iMouse.z>0.?sideview:carpos;
    vec3 ro = carpos;
    float fov = 0.6;
    vec3 rd = normalize(vec3(uvc,fov));

    if (iMouse.z>0.) {
        vec2 muv = (iMouse.xy - R/2.) / R.y;
        rd.yz = rotate(rd.yz,muv.y);
        rd.xz = rotate(rd.xz,muv.x);
    } else {
        // rd.yz = rotate(rd.yz,-0.5);
        float lookdown = smoothstep(29.,33.,carloop) - smoothstep(36.,41.,carloop);
        lookdown += smoothstep(70.,73.,carloop) - smoothstep(71.,81.,carloop);
        rd.yz = rotate(rd.yz,-lookdown);
        float twist = smoothstep(35.,45.,carloop) + smoothstep(75.,85.,carloop);
        rd.xy = rotate(rd.xy, twist*PI);

        rd.yz = rotate(rd.yz,nnoise2(vec3(t*0.1+20.)));
        rd.xz = rotate(rd.xz,nnoise2(vec3(t*0.1+80.)));
        // cam jitter - i'm preferring smooth look
        // rd.yz = rotate(rd.yz,nnoise(vec3(iTime*2.))*0.01);
        // rd.xz = rotate(rd.xz,nnoise(vec3(iTime*2.1+100.))*0.01);
        if (ro.y<0.) {
            rd.y *= -1.;
            ro.y *= -1.;
        }
    }

    // raymarch
    for (float M=0.;M<RAY_MAX;M++) { // multiple rays per pixel
        ro += hash31(M)/100000000.; // randomize
        MarchData d = raymarch(ro,rd);
        vec3 raycol = d.c.rgb;
        // col = vec3(1./d.d, d.s/STEP_MAX, float(d.h)); // debug march data
        // col = vec3(d.d);
        // lighting - should all of this happen within raymarch for each intersection? probably
        vec3 n = d.h?getNorm(d.p):vec3(0,0,1);
        n += hash33(d.p*100.)*.005; // scatter - ought to be based on material roughness
        vec3 bounceAngle = reflect(rd,n); 

        float amb = d.h?0.001:0.; // add ambient light for all hit pixels

        vec3 light1pos = vec3(d.p+1.);
        vec3 light1dir = normalize(light1pos-d.p);
        vec3 light2pos = vec3(d.p-vec3(.5,.9,1.));
        vec3 light2dir = normalize(light2pos-d.p);

        float diff1 = max(0.0, dot(n, light1dir)) * 1.;
        float spec1 = pow(max(0.0, dot(bounceAngle, light1dir)), SPECULAR_POWER) * SPECULAR_INTENSITY;
        // float fade = pow (1.0 - d / RAY_LENGTH_MAX, FADE_POWER);
        float light1result = diff1 + spec1;

        float diff2 = max(0.0, dot(n, light2dir)) * 1.;
        float spec2 = pow(max(0.0, dot(bounceAngle, light1dir)), SPECULAR_POWER) * SPECULAR_INTENSITY;
        // float fade = pow (1.0 - d / RAY_LENGTH_MAX, FADE_POWER);
        float light2result = diff2 + spec2;

        raycol *= vec3(amb + light1result + light2result); // apply lighting
        // raycol = vec3(amb + light1result + light2result); //d.c.rgb;
        col += raycol;
        // col = vec3(1./d.d);
    }
    col /= RAY_MAX;
    // post fx
    float gamma = 2.2;
    col = pow(col, vec3(1.0/gamma));
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define SS(a,b,c) smoothstep(a-b,a+b,c)

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

// sdfs
float sdBox(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdOrb(vec3 p, float b) {
  return length(p) - b;
}
float sdGyroid(vec3 p, float b) {
    float g = dot(sin(p),cos(p.yzx));
    return g - b;
}
// sdf ops
float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
float fOpUnionStairs(float a, float b, float r, float n) {
	float s = r/n;
	float u = b-r;
	return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2. * s)) - s)));
}


// hash - rand - noise
//--------------------
// 1 out, 1 in
float hash11(float p){
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
//  1 out, 3 in...
float hash13(vec3 p3){
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}
//  3 out, 1 in...
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}
///  3 out, 3 in...
vec3 hash33(vec3 p3){
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}
//
float mod289(float x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 mod289(vec4 x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 perm(vec4 x){return mod289(((x * 34.0) + 1.0) * x);}

float noise(vec3 p){
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}
