	vec4 dis = meta_triangle_sq1(p,p0,mid,p1,col);
	dis = dmin(dis,meta_triangle_sq1(p,p1,mid,p2,col1));
	dis = dmin(dis,meta_triangle_sq1(p,p2,mid,p0,col2));

	return dis;
}

