/*
 No. 4 in "Mriya" series
    "Mighty Mriya"                    (7llfz2)
    "Mriya Plans"                     (ftjBzG)
    "Mriya Over Metropolis"           (ct2SR1)
*/

#define AA  0   // (= 0/1) optional antialiasing

#if 1
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrCaps2Df (vec2 p, float r, float h);
float SmoothMax (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);

mat3 flyerMat;
vec4 csWm, csWt;
vec3 ltDir, qHit, flyerPos, dwf, dwr;
float tCur, dstFar, flSzFac, angWm, drpWm, angWt, drpWt;
int idObj;
bool wlDown;
const int idFace = 1, idEdge = 2, idFus = 11, idCkp = 12, idEng = 13, idWngM = 14, 
   idWngT = 15, idFin = 16, idWhl = 17, idLeg = 18, idFr = 19;
const float pi = 3.1415927;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float FlyerDf (vec3 p)
{
  vec3 q;
  vec2 ww;
  float dMin, d, s, w, t, wg;
  dMin = dstFar / flSzFac;
  p = flyerMat * (p - flyerPos) / flSzFac;
  q = p;
  w = 1.;
  s = 0.;
  if (q.z < -1.5) {
    s = q.z + 1.5;
    s *= s;
    w *= 1. - 0.025 * s;
    s *= 0.026;
  } else if (q.z > 3.5) {
    s = q.z - 3.5;
    s *= s;
    w *= 1. - 0.05 * s;
    s *= -0.02;
  }
  d = PrCapsDf (q - vec3 (0., s, 0.), w, 7.);
  if (wlDown) d = max (d, - min (PrBoxDf (vec3 (abs (q.x) - 0.45, q.y + 1., q.z - 0.4),
     vec3 (0.2, 0.25, 1.6)), PrBoxDf (vec3 (abs (q.x) - 0.2, q.y + 1., q.z - 4.8),
     vec3 (0.1, 0.3, 0.2))));
  DMINQ (idFus);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.65, 0.1, 6.);
  d = PrCapsDf (q, 0.02, 0.3);
  DMINQ (idFr);
  q = p;
  q.z -= -0.7;
  q.xz = abs (q.xz);
  q -= vec3 (0.2, 0.8, 4.);
  d = PrCapsDf (q.xzy, 0.07, 0.2);
  DMINQ (idFr);
  q = p;
  q.yz -= vec2 (0.4, 3.5);
  d = PrCapsDf (q, 0.6, 1.5);
  DMINQ (idCkp);
  ww = vec2 (4., 1.4);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (0.7, 1.3);
  q.xy = Rot2Cs (q.xy, csWm.xy);
  q.xz = Rot2Cs (q.xz, csWm.zw);
  t = ww.y * (1. - 0.3 * q.x / ww.x);
  s = 1.1 * (q.z - 0.2) / ww.y;
  d = SmoothMax (PrCaps2Df (q.yz, 0.15 * (t - s * s), t), abs (abs (p.x) - ww.x) - ww.x, 0.06);
  d = SmoothMax (d, - abs (PrBox2Df (vec2 (abs (p.x) - 6.9, q.z - 0.085 * q.x + 1.2),
     vec2 (0.85, 0.2))), 0.03);
  DMINQ (idWngM);
  for (float k = 0.; k < 4.; k ++) {
    q = p;
    q.x = abs (q.x);
    q -= (k + 1.1) * dwr + vec3 (0., 0.65, 0.1);
    d = PrCapsDf (q, 0.05, 0.25);
    DMINQ (idFr);
  }
  for (float k = 0.; k < 3.; k ++) {
    q = p;
    q.x = abs (q.x);
    q -= (k + 1.3) * dwf + vec3 (0., 0.22, 2.8);
    d = max (PrRoundCylDf (q.yzx - vec3 (0.25, -0.1, 0.), 0.25, 0.05, 0.), 0.3 - q.y);
    DMINQ (idFr);
    d = min (SmoothMax (abs (PrCapsDf (q, 0.3 * (1. - 0.5 * q.z * q.z), 0.9)) - 0.02,
       abs (q.z + 0.1) - 0.55, 0.02), PrCylDf (q, 0.28, 0.25));
    s = q.z + 0.2;
    d = max (min (d, PrCapsDf (q + vec3 (0., 0., 0.2), 0.25 * (1. - 2. * s * s), 0.7)),
       - PrCylDf (vec3 (q.xy, q.z + 0.8), 0.07, 0.2));
    DMINQ (idEng);
  }
  ww = vec2 (1.3, 0.85);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (0.8, -5.8);
  q.xy = Rot2Cs (q.xy, csWt.xy);
  q.xz = Rot2Cs (q.xz, csWt.zw);
  d = min (ww.x - abs (abs (p.x) - ww.x), abs (PrBox2Df (vec2 (abs (p.x) - 1.7,
     q.z - 0.07 * q.x + 0.6), vec2 (0.7, 0.25))));
  t = ww.y * (1. - 0.25 * q.x / ww.x);
  s = (q.z - 0.1) / ww.y;
  d = SmoothMax (PrCaps2Df (q.yz, 0.12 * (t - s * s), t), - d, 0.03);
  DMINQ (idWngT);
  ww = vec2 (1.5, 0.65);
  q = p;
  q.x = abs (q.x);
  q.xy = Rot2Cs (q.xy, sin (drpWt + vec2 (0.5 * pi, 0.)));
  q -= vec3 (2.7, 0.8, -6.9);
  t = ww.y * (1. - 0.25 * abs (q.y) / ww.x);
  s = (q.z + 0.1 * abs (q.y) / ww.x - 0.2) / ww.y;
  d = SmoothMax (PrCaps2Df (vec2 (q.x, q.z + 0.1 * abs (q.y) / ww.x), 0.1 * (t - s * s), t),
     abs (q.y - 0.35 * ww.x) - 0.65 * ww.x, 0.06);
  d = SmoothMax (d, - abs (PrBox2Df (vec2 (q.y - 0.8, q.z + 0.3), vec2 (0.5, 0.2))), 0.03);
  DMINQ (idFin);
  q.z -= -0.3;
  d = PrCapsDf (q, 0.05, 0.2);
  DMINQ (idFr);
  if (wlDown) {
    q = p;
    q.yz -= vec2 (-1., 0.4);
    t = 0.45;
    s = abs (q.z) - 3.5 * t;
    q.z = mod (q.z + 0.5 * t, t) - 0.5 * t;
    q.x = abs (q.x) - 0.5;
    d = max (PrRoundCylDf (vec3 (q.yz, abs (q.x) - 0.09), 0.16, 0.03, 0.02), s);
    DMINQ (idWhl);
    d = max (min (PrCylDf (q.yzx, 0.05, 0.09), PrCylDf (vec3 (q.xz, q.y - 0.2), 0.03, 0.2)), s);
    DMINQ (idLeg);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.2, -1.06, 4.8);
    d = PrRoundCylDf (vec3 (q.yz, abs (q.x) - 0.07), 0.1, 0.03, 0.01);
    DMINQ (idWhl);
    d = min (PrCylDf (q.yzx, 0.05, 0.07), PrCylDf (vec3 (q.xz, q.y - 0.2), 0.03, 0.2));
    DMINQ (idLeg);
  }
  return 0.6 * flSzFac * dMin;
}

vec3 DodecSym (vec3 p)
{
  vec2 csD;
  csD = sin (0.5 * atan (2.) + vec2 (0.5 * pi, 0.));
  p.xz = Rot2Cs (vec2 (p.x, abs (p.z)), csD);
  p.xy = Rot2D (p.xy, - pi / 10.);
  p.x = - abs (p.x);
  for (int k = 0; k <= 3; k ++) {
    p.zy = Rot2Cs (p.zy, vec2 (csD.x, - csD.y));
    p.y = - abs (p.y);
    p.zy = Rot2Cs (p.zy, csD);
    if (k < 3) p.xy = Rot2Cs (p.xy, sin (-2. * pi / 5. + vec2 (0.5 * pi, 0.)));
  }
  p.xy = sin (mod (atan (p.x, p.y) + pi / 5., 2. * pi / 5.) - pi / 5. +
     vec2 (0., 0.5 * pi)) * length (p.xy);
  p.xz = - vec2 (abs (p.x), p.z);
  return p;
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 t;
  float dMin, d, a1, a2, s, rEx;
  dMin = FlyerDf (p);
  rEx = 1.;
  t = vec2 (0.365, -1.);
  q = DodecSym (p);
  a1 = 0.5 * acos (-1. / sqrt (5.));
  a2 = 0.5 * acos (- (80. + 9. * sqrt (5.)) / 109.);
  s = abs (dot (q.yz, sin (a1 - a2 + vec2 (0., 0.5 * pi))) + 1.6 * rEx);
  d = SmoothMax (s - 0.08 * rEx, min (dot (q.yz, sin (a1 - pi + vec2 (0., 0.5 * pi))),
     dot (q.xy, sin (pi / 5. + vec2 (0.5 * pi, 0.)))) - 0.01 * rEx, 0.02);
  DMIN (idEdge);
  q.yz -= 9. * t * rEx;
  d = abs (length (q) - 8. * rEx) - 0.05 * rEx;
  DMIN (idFace);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 FlyerCol ()
{
  vec4 col4, colY4, colB4;
  vec2 s;
  float t;
  col4 = vec4 (1., 1., 0.95, 0.2);
  colY4 = vec4 (0.8, 0.8, 0., 0.2);
  colB4 = vec4 (0.2, 0.2, 1., 0.2);
  if (idObj == idFus) {
    col4 = mix (colB4, col4, smoothstep (0.07, 0.08, abs (qHit.y + 0.6)));
    col4 = mix (colY4, col4, smoothstep (0.07, 0.08, abs (qHit.y + 0.35)));
    if (PrCylDf (qHit - vec3 (0., 0., 0.4), 0.98, 1.7) < 0. ||
       abs (qHit.x) < 0.4 && qHit.y < 0. && PrCylDf (qHit - vec3 (0., 0., 4.7), 0.93, 0.3) < 0.)
       col4 *= vec4 (vec3 (0.2), 0.);
    if (! wlDown && qHit.y < 0.) col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.012, 
       min (abs (PrBox2Df (vec2 (abs (abs (qHit.x) - 0.45) - 0.1, qHit.z - 0.4), vec2 (0.1, 1.6))),
       abs (PrBox2Df (vec2 (abs (abs (qHit.x) - 0.2) - 0.05, qHit.z - 4.8), vec2 (0.05, 0.2)))));
    col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.01, abs (PrRoundBox2Df (qHit.yz - vec2 (-0.4, 3.5),
       vec2 (0.3, 0.15), 0.05)));
  } else if (idObj == idCkp) {
    if (qHit.z > 0.4 && abs (qHit.y - 0.45) < 0.07 && abs (abs (qHit.x) - 0.2) > 0.03 &&
       abs (abs (qHit.z - 1.35) - 0.2) > 0.03) col4 = vec4 (0., 0., 0., -1.);
  } else if (idObj == idEng) {
    t = length (qHit.xy);
    if (PrCapsDf (qHit, 0.3 * (1. - 0.5 * qHit.z * qHit.z), 0.85) < 0.) {
      if (qHit.z > 0. && t < 0.14) col4 = vec4 (1., 0., 0., 0.2);
      else col4 *= vec4 (vec3 (0.3), 0.);
    } else {
      if (abs (qHit.z + 0.1) > 0.5) col4 = colY4;
      else col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (qHit.z - 0.3)));
    }
  } else if (idObj == idWngM) {
    t = qHit.z + 0.1 * abs (qHit.x);
    col4 = mix (colB4, col4, smoothstep (0.04, 0.05, abs (t - 1.2)));
    col4 = mix (colY4, col4, smoothstep (0.04, 0.05, abs (t - 1.)));
    if (qHit.y < 0.) {
      if (abs (abs (qHit.x) - 3.9) < 2.1) col4 *= 0.7 + 0.3 *
         smoothstep (0.008, 0.012, abs (qHit.z - 0.1 * abs (qHit.x) + 1.));
    } else {
      s = Rot2D (qHit.xz - vec2 (3.5, -0.3), -0.1 * pi);
      s.x = abs (abs (s.x) - 0.8) - 0.3;
      col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.012, abs (PrBox2Df (s, vec2 (0.3, 0.15))));
    }
  } else if (idObj == idWngT) {
    t = qHit.z + 0.15 * abs (qHit.x);
    col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (t - 0.7)));
    col4 = mix (colY4, col4, smoothstep (0.02, 0.03, abs (t - 0.6)));
  } else if (idObj == idFin) {
    t = qHit.z + 0.15 * abs (qHit.y);
    col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (t - 0.4)));
    col4 = mix (colY4, col4, smoothstep (0.02, 0.03, abs (t - 0.3)));
  } else if (idObj == idWhl) {
    col4 = mix (vec4 (0.7, 0.7, 0.75, 0.1), vec4 (0.3, 0.3, 0.3, 0.),
       step (0.07, length (qHit.yz)));
  } else if (idObj == idLeg) {
     col4 = vec4 (0.8, 0.8, 0.85, 0.1);
  }
  return col4;
}

void FlyerParms ()
{
  angWm = 0.1 * pi;
  drpWm = 0.02 * pi;
  csWm = vec4 (sin (drpWm + vec2 (0.5 * pi, 0.)), sin (angWm + vec2 (0.5 * pi, 0.)));
  angWt = 0.12 * pi;
  drpWt = -0.03 * pi;
  csWt = vec4 (sin (drpWt + vec2 (0.5 * pi, 0.)), sin (angWt + vec2 (0.5 * pi, 0.)));
  dwr = vec3 (1.4, 0., 0.);
  dwr.xy = Rot2Cs (dwr.xy, sin (- drpWm + vec2 (0.5 * pi, 0.)));
  dwr.xz = Rot2Cs (dwr.xz, sin (-0.7 * angWm + vec2 (0.5 * pi, 0.)));
  dwf = vec3 (1.5, 0., 0.);
  dwf.xy = Rot2Cs (dwf.xy, sin (-0.9 * drpWm + vec2 (0.5 * pi, 0.)));
  dwf.xz = Rot2Cs (dwf.xz, sin (-1.3 * angWm + vec2 (0.5 * pi, 0.)));
  wlDown = (mod (0.05 * tCur, 2.) > 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, bgCol, vn;
  float dstObj, nDotL;
  int nRef;
  const int maxRef = 3;
  bgCol = vec3 (0.3, 0.3, 0.35);
  FlyerParms ();
  col = bgCol;
  nRef = 0;
  for (int k = VAR_ZERO; k <= maxRef; k ++) {
    dstObj = ObjRay (ro, rd);
    if (dstObj < dstFar && idObj == idFace) {
      ro += dstObj * rd;
      rd = reflect (rd, ObjNf (ro));
      ro += 0.001 * rd;
      nRef = k;
    } else break;
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == idEdge) col4 = vec4 (0.8, 0.7, 0.6, 0.1);
    else col4 = FlyerCol ();
    nDotL = max (dot (vn, ltDir), 0.);
    nDotL *= nDotL;
    if (col4.a < 0.) col4.a = 0.2;
    col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, ltDir), 0.) +
       0.8 * nDotL) + col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
    col = mix (bgCol, col, pow (0.8, 10. * float (nRef) / float (maxRef)));
  }
  return clamp (col, 0., 1.);
}

void FlyerPM (out vec3 flPos, out mat3 flMat)
{
  vec3 flVd;
  vec2 cs;
  float oRl;
  flPos = vec3 (0., 0.05, 0.);
  flVd = vec3 (1., 0., 0.);
  oRl = 0.1 * pi * sin (0.2 * tCur);
  cs = sin (oRl + vec2 (0.5 * pi, 0.));
  flMat = mat3 (cs.x, - cs.y, 0., cs.y, cs.x, 0., 0., 0., 1.) *
     mat3 (flVd.z, 0., flVd.x, 0., 1., 0., - flVd.x, 0., flVd.z);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  flSzFac = 0.1;
  FlyerPM (flyerPos, flyerMat);
  az = 0.;
  el = -0.05 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az -= 0.02 * pi * tCur;
    el -= 0.15 * pi * sin (0.01 * pi * tCur);
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -1.5);
  zmFac = 2.2;
  dstFar = 50.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (p - vec2 (0., clamp (p.y, - h, h))) - r;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}
