float dot2( in vec2 v ) { return dot(v,v); }
float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

vec3 palette( in float t)
{
    vec3 a = vec3(1.168, -0.552, 0.618); 
    vec3 b = vec3(-0.719, 0.597, 0.234);
    vec3 c = vec3(0.350, 0.350, 0.350);
    vec3 d = vec3(0.000, 0.333, 0.667);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)*2.-1.;
    uv.x *= iResolution.x/iResolution.y;
    uv.y += 0.6;
    vec3 color = vec3(0.);

    for(int i = 0; i < 2; i++){
        float dist = sdHeart(uv)+ float(i);

        dist= abs(cos(dist*8.+iTime)/8.)*0.5;
        dist+= smoothstep(0.2,3., sdHeart(uv));

        color += 0.03/length(uv-vec2(0.0,0.6)+vec2(2,1.5))*palette(length(uv)*float(i));
        color += 0.03/length(uv-vec2(0.0,0.6)+vec2(2,-1.5))*palette(length(uv)*float(i));
        color += 0.03/length(uv-vec2(0.0,0.6)+vec2(-2,1.5))*palette(length(uv)*float(i));
        color += 0.03/length(uv-vec2(0.0,0.6)+vec2(-2,-1.5))*palette(length(uv)*float(i));
        color += 0.02/smoothstep(0.,0.1,dist) * palette(length(uv)*float(i));
    }


    fragColor = vec4(color,1);

}
