#define n_tau 6.2831
float f_n_rand_dave_hoskins(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float f_n_noise_interpolated(
    vec2 o
){
    vec2 o_xp0yp0 = o + vec2(0.,0.);
    vec2 o_xp0yp1 = o + vec2(0.,1.);
    vec2 o_xp1yp0 = o + vec2(1.,0.);
    vec2 o_xp1yp1 = o + vec2(1.,1.);

    float n_xp0yp0 = f_n_rand_dave_hoskins(floor(o_xp0yp0));
    float n_xp0yp1 = f_n_rand_dave_hoskins(floor(o_xp0yp1));
    float n_xp1yp0 = f_n_rand_dave_hoskins(floor(o_xp1yp0));
    float n_xp1yp1 = f_n_rand_dave_hoskins(floor(o_xp1yp1));

    vec2 o_fract = fract(o);
    float n1 = 
        (1.-o_fract.x) * n_xp0yp0
        + o_fract.x * n_xp1yp0;
    float n2 = 
        (1.-o_fract.x) * n_xp0yp1
        + o_fract.x * n_xp1yp1;
    float n3 = 
        (1.-o_fract.y) * n1
        + o_fract.y * n2;

    //float n_1 = mix(n_xp0yp0, n_xp1yp0, (o_fract.x));
    //float n_2 = mix(n_xp0yp1, n_xp1yp1, (o_fract.x));
    //float n_3 = mix(n_1, n_2, (o_fract.y));
    return n3; 
}
float f_n_noise_interpolated_stacked(
    vec2 o, 
    float n_its, 
    float n_it_factor_freq,
    float n_it_factor_amp
){
    float n_freq = 1.;
    float n_amp = 1.;
    float n_sum = 0.;
    float n_factor_amp_sum = 0.;
    for(float n_it = 0.; n_it < n_its; n_it+=1.){
        n_freq *= n_it_factor_freq; 
        n_amp *= n_it_factor_amp;
        n_factor_amp_sum += n_amp;
        n_sum += f_n_noise_interpolated(o*n_freq)*n_amp;
    }
    return n_sum/n_factor_amp_sum;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
