        vec2(
            sin((n_ang_nor+.3+iTime*0.2)*n_tau)*1.,
            cos((n_ang_nor+.3+iTime*0.2)*n_tau)*1.
        ),
        //o_fc*sin(iTime)*20.,
        20., 
        1.2, 
        1./(1.2)
    );
    float n_dist = length(
        o_fc
        + (n_noise_interpolated_stacked-.5) * 0.2
    )/2.;
    float n_circ;

    n_circ = smoothstep(0.2, 0., n_dist);



