const vec3 vWaterFogColor = Gamma( vec3( 0.9, 0.4, 0.3 ) ) * 16.0;
const vec3 vFoamColor = Gamma( vec3( 0.9, 0.9, 0.85 ) );
const vec3 vSkyColor = Gamma( vec3( 0.01, 0.4, 0.8 ) );
const vec3 vSunColor = Gamma( vec3( 1.0, 0.8, 0.5 ) );
const vec3 vTerrainColor0 = Gamma( vec3(1.0,0.88,0.7)*0.8 );
const vec3 vTerrainColor1 = Gamma( vec3(0.9,0.9,0.8)*0.9 );
#define vLightDir normalize( vec3( 0.0, 0.21, -1.0 ) )
const vec3 vLookDir = vec3( 0.0, 0.0, -1.0 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 vTexC = textureLod(iChannel0,uv,0.0);

    vec4 vTexL = textureLodOffset(iChannel0,uv, 0.0, ivec2(-1,0));
    vec4 vTexR = textureLodOffset(iChannel0,uv, 0.0, ivec2(1,0));
    vec4 vTexT = textureLodOffset(iChannel0,uv, 0.0, ivec2(0,-1));
    vec4 vTexB = textureLodOffset(iChannel0,uv, 0.0, ivec2(0,1));

    float hC = vTexC.z+vTexC.w;
	float hL = vTexL.z+vTexL.w;
	float hR = vTexR.z+vTexR.w;
	float hT = vTexT.z+vTexT.w;
	float hB = vTexB.z+vTexB.w;

    float fMinZ = min( min( min( min( vTexC.z, vTexL.z ), vTexR.z ), vTexT.z ), vTexB.z );
    float fMaxZ = max( max( max( max( vTexC.z, vTexL.z ), vTexR.z ), vTexT.z ), vTexB.z );

    float fAlpha = min(1.0,vTexC.z*130.0);

    vec3 vNormal = vec3( ( hR - hL )*g_fGridSizeInMeter, ( hB - hT )*g_fGridSizeInMeter, 2.0 );
    vNormal = normalize( vNormal );
    vec2 vTerrainUV = uv;
    vec2 vRefractUV = vTerrainUV - vNormal.xy*vTexC.z*6.0;

    vec3 vTerrainColor = mix( vTerrainColor0, vTerrainColor1, SampleColor( iChannel1, vRefractUV, iResolution.xy ) );
    vTerrainColor *= 1.0-min(1.0,fMaxZ*80.0)*0.2;
    vec3 vRefract = vTerrainColor;
    vec4 vTexCRefract = textureLod(iChannel0,vRefractUV,0.0);
    vTexCRefract = mix( vTexC, textureLod(iChannel0,vRefractUV,0.0), min( vTexCRefract.z*1.0, 1.0 ) );
    vec3 vFog = 1.0 - exp( -vTexCRefract.zzz/(vNormal.z*0.9999)*vWaterFogColor );
    vRefract *= ( 1.0 - vFog );



    vec3 vReflect = ( pow( ( 1.0 - pow( vNormal.z*0.9999999, 100.0 ) ), 0.4 ) )* 1.1 * vSkyColor;
    //vec3 vReflect = ( 1.0-pow( ( 1.0 - pow( vNormal.z*0.9999999, 1000.0 ) ), 0.1 ) )* 0.3 * vSkyColor;
    vec3 vHalfVec = normalize( vLookDir + vLightDir );
    float fHdotN = max( 0.0, dot( -vHalfVec, vNormal ) );
    vReflect += pow( fHdotN , 1200.0 ) * 20.0 * vSunColor;
    vReflect += pow( fHdotN , 180.0 ) * 0.5 * vSkyColor;

    float fLight = pow( max( dot( vNormal, -vLightDir ), 0.0 ), 10.0 );    


    float fFoam = max(0.0,1.0-fMinZ*8.0)*0.3;

    vec3 vWater = mix( vRefract*fLight + vReflect, vFoamColor, vec3(fFoam,fFoam,fFoam) );    
    //vWater = vReflect;



    vec3 vOut = mix( vTerrainColor*fLight, vWater, vec3(fAlpha) );

    /*if ( iResolution.x > 1180.0 )
    {
        if ( uv.y < 0.3 ) vOut = mix( vec3(vTexC.w*0.1), vec3( vTexC.xy+0.5, 0.0 ), vec3(fAlpha) );
        vOut *= smoothstep( 0.0, 0.004, abs(uv.y-0.3) );
    }*/   

    //vOut = vTerrainColor;

    // button!
    if ( fragCoord.x < 40.0 && fragCoord.y < 40.0 ) vOut = mod( iTime, 1.0 ) < 0.5 ? vec3( 0.4, 0.2, 0.2 ) : vec3( 0.3, 0.2, 0.1 );



    fragColor = vec4( DeGamma( vOut ), 1.0 );
}

// Velocity Advection

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 tc = ivec2(fragCoord);    
    vec4 vTexC = texelFetch(iChannel0,tc,0);    

    vec2 uv = fragCoord/iResolution.xy;
    //vec2 vOffset = vTexC.xy/iResolution.xy * g_fAdvectSpeed * g_fElapsedTimeInSec / g_fGridSizeInMeter;
    float dt = -g_fAdvectSpeed * g_fElapsedTimeInSec / g_fGridSizeInMeter;
    vec2 v1 = vTexC.xy;
    vec2 v2 = textureLod(iChannel0, uv - ( 0.5 * v1 * dt ) / iResolution.xy, 0.0 ).xy;
    vec2 v3 = textureLod(iChannel0, uv - ( 0.5 * v2 * dt ) / iResolution.xy, 0.0 ).xy;
    vec2 v4 = textureLod(iChannel0, uv - ( v3 * dt ) / iResolution.xy, 0.0 ).xy;
    vec2 v = (1.0 * v1 + 2.0 * v2 + 2.0 * v3 + 1.0 * v4) / 6.0;


    vec4 vTex = textureLod(iChannel0,uv + ( v * dt ) / iResolution.xy,0.0);

    fragColor = vTexC;
}

// Height Integration

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   ivec2 tc = ivec2(fragCoord);    
    vec4 vTexC = texelFetch(iChannel0,tc,0);    
    vec4 vTexL = ( tc.x > 0 ) ?                    texelFetchOffset(iChannel0,tc, 0, ivec2(-1,0)) : vTexC*vec4(0.0,0.0,1.0,1.0);
    vec4 vTexR = ( tc.x < int(iResolution.x)-1 ) ? texelFetchOffset(iChannel0,tc, 0, ivec2(1,0)) : vTexC*vec4(0.0,0.0,1.0,1.0);
    vec4 vTexT = ( tc.y > 0 ) ?                    texelFetchOffset(iChannel0,tc, 0, ivec2(0,-1)) : vTexC*vec4(0.0,0.0,1.0,1.0);
    vec4 vTexB = ( tc.y < int(iResolution.y)-1 ) ? texelFetchOffset(iChannel0,tc, 0, ivec2(0,1)) : vTexC*vec4(0.0,0.0,1.0,1.0);

    vec2 vOffset = vTexC.xy/iResolution.xy * g_fAdvectSpeed * g_fElapsedTimeInSec / g_fGridSizeInMeter;

    vec2 uv = fragCoord/iResolution.xy;
    vec4 vTex = textureLod(iChannel0,uv+vOffset,0.0);

    if ( vTexC.x == 0.0 ) vTex.xyz = vTexC.xyz;
	if ( vTexC.y == 0.0 ) vTex.xyz = vTexC.xyz;

    vTex.zw = vTexC.zw;

    float fxL = vTexL.x;
	float fxR = vTexC.x;	
	float fyT = vTexT.y;
	float fyB = vTexC.y;

    // We also found that it yields a more stable simulation:
	float hL = ( vTexL.x >= 0.0 ) ? vTexL.z : vTexC.z;
	float hR = ( vTexC.x <= 0.0 ) ? vTexR.z : vTexC.z;
	float hT = ( vTexT.y >= 0.0 ) ? vTexT.z : vTexC.z;
	float hB = ( vTexC.y <= 0.0 ) ? vTexB.z : vTexC.z;

   	{
		// 2.2. Overshooting Reduction
		float beta = 2.0;
		float hAvgMax = beta * g_fGridSizeInMeter / ( g_fG * (g_fElapsedTimeInSec) );
		float hAdj = max( 0.0, (vTexL.z+vTexR.z+vTexT.z+vTexB.z)/4.0 - hAvgMax );

		hL -= hAdj;
		hR -= hAdj;
		hT -= hAdj;
		hB -= hAdj;
	}

    float dH = -( (hR*fxR-hL*fxL) / g_fGridSizeInMeter + (hB*fyB-hT*fyT) / g_fGridSizeInMeter );

	vTexC.z += dH * (g_fElapsedTimeInSec);

    fragColor = vTexC;
}

// Velocity Integration

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 tc = ivec2(fragCoord);    
    vec4 vTexC = texelFetch(iChannel0,tc,0);    
    //vec4 vTexL = ( tc.x > 0 ) ?                    texelFetchOffset(iChannel0,tc, 0, ivec2(-1,0)) : vTexC*vec4(0.0,0.0,1.0,1.0);
    vec4 vTexR = ( tc.x < int(iResolution.x)-1 ) ? texelFetchOffset(iChannel0,tc, 0, ivec2(1,0)) : vTexC*vec4(0.0,0.0,1.0,1.0);
    //vec4 vTexT = ( tc.y > 0 ) ?                    texelFetchOffset(iChannel0,tc, 0, ivec2(0,-1)) : vTexC*vec4(0.0,0.0,1.0,1.0);
    vec4 vTexB = ( tc.y < int(iResolution.y)-1 ) ? texelFetchOffset(iChannel0,tc, 0, ivec2(0,1)) : vTexC*vec4(0.0,0.0,1.0,1.0);


    ////////////////////////////////////////////////////////////////


    float zC = vTexC.z+vTexC.w;
	//float zL = vTexL.z+vTexL.w;
	float zR = vTexR.z+vTexR.w;
	//float zT = vTexT.z+vTexT.w;
	float zB = vTexB.z+vTexB.w;

	vec2 vV;
	vV.x = -g_fG / g_fGridSizeInMeter * ( zR - zC );
	vV.y = -g_fG / g_fGridSizeInMeter * ( zB - zC );
	vTexC.xy += vV * g_fElapsedTimeInSec;

    // 2.1.4. Boundary Conditions

	if (	( ( vTexC.z <= EPS*g_fGridSizeInMeter ) && ( vTexC.w > zR ) ) || 
			( ( vTexR.z <= EPS*g_fGridSizeInMeter ) && ( vTexR.w > zC ) ) )
	{
		vTexC.x = 0.0;
	}

	if (	( ( vTexC.z <= EPS*g_fGridSizeInMeter ) && ( vTexC.w > zB ) ) || 
			( ( vTexB.z <= EPS*g_fGridSizeInMeter ) && ( vTexB.w > zC ) ) )
	{
		vTexC.y = 0.0;
	}

    // We also clamp the magnitudes
    float l = length( vTexC.xy );
	if ( l > 0.0 )
	{
		float alpha = 0.5;
		vTexC.xy /= l;
		l = min( l, g_fGridSizeInMeter / (g_fElapsedTimeInSec) * alpha );
		vTexC.xy *= l;
	}

    // hack blur
    /*{
        float fMinH = min( min( min( zL, zR ), min( zT, zB ) ), zC );
        float fMaxH = max( max( max( zL, zR ), max( zT, zB ) ), zC );
        float fW = clamp( ( fMaxH - fMinH )*g_fGridSizeInMeter/g_fHackBlurDepth, 0.0, 1.0 );

        float fTexLW = min( (zL-zC)*(1.0/4.0), vTexL.z );
        float fTexRW = min( (zR-zC)*(1.0/4.0), vTexR.z );
        float fTexTW = min( (zT-zC)*(1.0/4.0), vTexT.z );
        float fTexBW = min( (zB-zC)*(1.0/4.0), vTexB.z );

        float fTexAddition = fTexLW + fTexRW + fTexTW + fTexBW;
        vTexC.z += fTexAddition*0.99*fW;
    }*/

    // 2.1.5. Stability Enhancements
    if ( vTexC.z <= 0.0 )
    {
        vTexC.z = 0.0;
    }

    ////////////////////////////////////////////////////////////////


    vec2 uv = fragCoord/iResolution.xy;    
    vTexC.w = SampleDepth( iChannel1, uv, iResolution.xy );

    // click
    if ( iMouse.z > 0.0 )
    {
        float l = length((fragCoord-iMouse.xy)*0.001);
        l *= 20.0;
        l = clamp( 1.0 - l, 0.0, 1.0 );
        vTexC.z += 0.01 * ( cos( l * PI ) * -0.5 + 0.5 );
    }

    // reset
    if ( ( iFrame <= 1 ) || ( iMouse.z > 0.0 && iMouse.x < 40.0 && iMouse.y < 40.0 ) )
    {
        vTexC.xy = vec2(0.0);

        vTexC.z = max( 0.0 - vTexC.w, 0.0 );

        // jon a cunami.
        float l = abs( uv.x - 0.95 );
        l *= 10.0;
        l = clamp( 1.0 - l, 0.0, 1.0 );
        vTexC.z += 0.1 * ( cos( l * PI ) * -0.5 + 0.5 );
    }

    // jon a cunami.
    float ll = abs( uv.x - 0.95 );
    ll *= 20.0;
    ll = clamp( 1.0 - ll, 0.0, 1.0 );
    vTexC.z += 0.001 * ( cos( ll * PI ) * -0.5 + 0.5 ) * sin( iTime*1.6 ) * max( 0.0, (10.0-iTime)/10.0 );   


    fragColor = vTexC;
}

