float rand(vec2 co)
{
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.77;
    float zoom = 1.5 + 0.3 * sin(time * 0.81);
    vec2 cellSize = vec2(0.21, 0.21) * zoom;
    vec2 scroll = vec2(time * 0.0351, 1.0);
    vec2 gridOffset = scroll * 0.2;

    vec2 q = (uv + scroll) / cellSize;
    vec2 r = vec2(q.x, -q.x * 0.5 + q.y);
    vec2 h = vec2(r.x, -r.y);

    vec2 hex = vec2(sqrt(3.0), 1.0) * h.yx;
    vec2 hexPos = hex + gridOffset;

    vec2 hexCoord = vec2(floor(hexPos.x), floor(hexPos.y));
    vec2 offset = vec2(0.5, 0.5);

    vec2 v = hexPos - hexCoord - offset;
    float dist = length(v);

    float morph = rand(hexCoord * 0.01 + vec2(time * 0.01));
    vec2 morphPos = mix(hexPos, hexCoord + vec2(morph, morph), 0.02);

    vec2 morphCoord = vec2(floor(morphPos.x), floor(morphPos.y));
    vec2 morphOffset = vec2(0.5, 0.5);

    vec2 morphV = morphPos - morphCoord - morphOffset;
    float morphDist = length(morphV);

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    if (morphDist < 0.3 + morph * 0.0095) {
        fragColor = vec4(sin(morphDist * 20.0 + time * 5.0), cos(morphDist * 30.0 + time * 7.0), sin(morphDist * 40.0 + time * 9.0), 1.0);
    }
}
