vec3 WHITE = vec3(1);
vec3 BLUE = vec3(0, 47.0/255.0, 108.0/255.0);

vec3 flag(vec2 uv)
{
    // Finnish flag ratios y: 4:3:4, x: 5:3:10.

    // Resolution independent width.
    float w = fwidth(uv.y)*3.;
    float stripeVert = smoothstep(0.894, 0.894+w, sin(uv.y * 3.14));

    w = fwidth(uv.x)*3.;
    float stripeHoriz = smoothstep(0.966, 0.966+w, sin(uv.x * 3.14 + .434));

    vec3 col = mix(WHITE, BLUE, stripeVert); 
    col *= mix(WHITE, BLUE, stripeHoriz); 
    col = mix(WHITE, BLUE, max(stripeHoriz, stripeVert));

    // The flag goes outside of zero to one range when it waves. Make the bottom edge to rest at zero.
    col *= smoothstep(w, 0.0, abs(uv.y - 0.5) - 0.5);

    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1).
    vec2 uv = fragCoord/iResolution.xy;

    // Make it wavy.
    float t = uv.x*4.5-1.4*iTime+uv.y*2.0;
    uv.y -= sin(t)*0.1;

    vec3 col = flag(uv);

    // Add shading. The light comes from the side, so it should be dark when it's going
    // down and bright when it's going up. We get that by taking the derivate of the value
    // set to uv.y which is cosine of t.   
    col *= 0.8 + cos(t) * 0.2;

    fragColor = vec4(col, 1.0);
}
