#ifdef GL_ES
precision mediump float;
#endif

#ifndef PI
#define PI 3.1415926535897932384626433832795
#endif

#ifndef TAU
#define TAU 6.2831853071795864769252867665590
#endif

vec2 rotate(vec2 v, float a) {
	float sinA = sin(a);
	float cosA = cos(a);
	return vec2(v.x * cosA - v.y * sinA, v.y * cosA + v.x * sinA); 	
}

float poly(vec2 uv, float d) {
    float a = atan(uv.x,uv.y)+PI;
    float r = length(uv);
    float v = TAU/float(3);
    float final = cos(floor(.5+a/v)*v-a)*r;
    return max(abs(final), abs(final)) - d;
}

float smootheststep(float edge0, float edge1, float x)
{
    x = clamp((x - edge0)/(edge1 - edge0), 0.0, 1.0) * 3.14159265;
    return 0.5 - (cos(x) * 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv * 2. - 1.0;
	uv.x *= iResolution.x / iResolution.y;
	uv *= 2.;

    float blurAmount = -0.005 * 1080.0 / iResolution.y;

	float period = 25.0;
	float time = iTime / period;
	time = mod(time, 1.0);
	time = smootheststep(0.0, 1.0, time);


	fragColor = vec4(0.0, 0.0, 0.0, 1.0);

	for (int i = 0; i < 25; i++) {
		float n = float(i);
		float size = 1.0 - n / 25.0;
		float rotateAmount = (n * 0.5 + 0.25) * PI * 2.0; 

        float animTime =  time;
        if(n < 1.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.467, 0.051, 1.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 2.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.184, 0.047, 0.91), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 3.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0., 0.11, 1.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 4.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0., 0.345, 0.91), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 5.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.0, 0.6, 0.941), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 6.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.051, 0.663, 1.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 7.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.0047, 0.835, 0.91), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 8.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0., 1., 0.808), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 9.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0., 0.91, 0.471), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 10.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.063, 0.91, 0.047), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 11.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.369, 1.0, 0.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 12.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.651, 0.91, 0.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 13.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(1., 0.98, 0.051), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 14.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.91, 0.808, 0.047), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 15.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(1., 0.78, 0.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 16.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.91, 0.62, 0.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 17.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(1., 0.6, 0.051), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 18.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.91, 0.424, 0.047), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 19.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(1., 0.29, 0.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 20.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.91, 0.125, 0.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 21.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(1., 0.051, 0.078), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 22.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.91, 0.047, 0.506), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 23.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.98, 0., 1.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 24.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.647, 0., 0.91), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        } else if(n < 25.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.467, 0.051, 1.), smoothstep(0.0, blurAmount, poly(rotate(uv, -rotateAmount * animTime), size)));
        }

		float blackOffset = mix(1.0 / 4.0, 1.0 / 2.0, n / 25.) / 25.; // 25


        fragColor.rgb = mix(fragColor.rgb, vec3(0.0, 0.0, 0.0), smoothstep(0.0, blurAmount, poly(rotate(uv, -(rotateAmount + PI / 2.0) * time), size - blackOffset)));
    }
}
