float f_n_limited(
    float n_min, 
    float n_max, 
    float n
){
    return 
        max(min(n_min,n_max), min(max(n_min, n_max),n));
}
float f_n_limited2(
    float n_min, 
    float n_max, 
    float n
){
    float n_tmp = n_min;
    n_min = min(n_min,n_max);
    n_max = max(n_max, n_tmp);
    if(n < n_min) return 0.;
    if(n < n_max) return n ;
    if(n > n_max) return 1.;
}
