renderpass.code:
float circle(vec2 xy, vec2 center,float R){
    vec2 r = center -xy; 
    return step( dot(r,r),R * R );
}

float square(vec2 xy, vec2 center){
    vec2 d = center -xy; 
    vec2 dd= step( abs(d),vec2(.2) );
    return dd.x* dd.y ;
}


void mainImage( out vec4 O, vec2 U )
{  
float ratio  = iResolution.x/iResolution.y;
vec2 uv = U/iResolution.xy ;
uv.x*=ratio;

vec2 mouse = iMouse.xy/iResolution.xy ;
mouse.x*= ratio ;
float mz =  step(0. ,iMouse.z);// edge, val z>0 mz 就等于1
float mw =  step(0. ,iMouse.w);
   vec3 color = vec3(.8,.5,.6) ;
   vec3 color2 = vec3(.1,.1,.6) ;
   vec3 color3 = vec3(uv,.6) ;
   color= mix(color, color2 + mw*vec3(.5),circle(uv, vec2(.4,.5), .3)) ;
   color= mix(color, color2 + mz*vec3(.7),square(uv, vec2(.8,.5))) ;
   color= mix(color, color3 ,circle(uv, mouse,.1)) ;
   O = vec4(color, 1.);

}
