void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 o_mou_coo_nor = iMouse.xy/iResolution.xy;
    vec2 uv_big_frac = fract(uv*(o_mou_coo_nor.x*10.));
    if(iMouse.w == 0.0){
        o_mou_coo_nor = vec2(0.0, 0.5);
    }
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 o_col_img = texture(iChannel0, uv);
    // Output to screen
    float n_colors = 2.;
    float n_expnt_max = 8.;
    float n_expnt_min = 1.;
    float n_expnt_range = n_expnt_max - n_expnt_min;
    float n_numerus = 0.;
    for(float n_expnt = n_expnt_min; n_expnt < n_expnt_max; n_expnt+=1.){
        n_numerus = pow(2., n_expnt);
        if(uv_big_frac.x > ((1./n_expnt_range)*(n_expnt-n_expnt_min))){

            n_colors = n_numerus;
            fragColor = floor(o_col_img*n_colors)/(n_colors);
            if(uv.y > o_mou_coo_nor.y){
                fragColor = o_col_img;
            }
        }

    }


}
