    if(READ_TEXTURE_COND) res = iChannelResolution[0].xy;

    if(uv.x >= res.x) if(uv.x < res.x + 8.0) uv.x -= res.x; else uv.x -= iResolution.x - res.x;
    if(uv.y >= res.y) if(uv.y < res.y + 8.0) uv.y -= res.y; else uv.y -= iResolution.y - res.y;

   #if 1
    // jittering
    if(DO_DOWNSAMPLE_COND)
    {
        uint h = uint(iFrame) / 2u;

        bool a = (h & 1u) != 0u;
        bool b = (h & 2u) != 0u;

        if(a != b) uv.x += 1.0;
        if(b     ) uv.y += 1.0;

        if(uv.x == res.x) uv.x = 0.0;
        if(uv.y == res.y) uv.y = 0.0;
    }
   #endif

    ivec2 uvi = ivec2(uv);

    float noise = Hash01(uv, 0u);

    vec3 col = vec3(noise);

    if(READ_TEXTURE_COND)
    {
        col = texelFetch(iChannel0, uvi, 0).rgb;

        col = pow(col, vec3(2.2));    
    }

    if(ReadKeyToggle(KEY_CTRL) != 0.0) col = vec3(col.r > 0.99 ? 1.0 : 0.0);

    outCol = vec4(col, filterType);
}

